CREATE FUNCTION adempiere.add_business_day(from_date date, num_days integer) RETURNS date
    LANGUAGE sql
    AS $$
select d
    from (
        select d::date, row_number() over (order by d)
        from generate_series(from_date+ 1, from_date+ num_days* 2+ 5, '1d') d
        where 
            extract('dow' from d) not in (6) 
        ) s
    where row_number = num_days
$$;


