CREATE FUNCTION adempiere.altercolumn(tablename name, columnname name, datatype name, nullclause character varying, defaultclause character varying) RETURNS void
    LANGUAGE plpgsql
    AS $$
declare
   command text;
   viewtext text[];
   viewname name[];
   dropviews name[];
   perms   text[];
   privs text;
   i int;
   j int;
   v record;
   sqltype       text;
   sqltype_short text;
   typename name;
   namespace text;
begin
   namespace := 'adempiere';
   if datatype is not null then
	select pg_type.typname, format_type(pg_type.oid, pg_attribute.atttypmod)
            into typename, sqltype
            from pg_class, pg_attribute, pg_type, pg_namespace
            where relname = lower(tablename)
                and relkind in ('r','p')
                and pg_class.oid = pg_attribute.attrelid
                and attname = lower(columnname)
                and atttypid = pg_type.oid
                and pg_class.relnamespace = pg_namespace.oid
                and pg_namespace.nspname = namespace;
        sqltype_short := sqltype;
        if typename = 'numeric' then
	   sqltype_short := replace(sqltype, ',0', '');
        elsif strpos(sqltype,'character varying') = 1 then
	   sqltype_short := replace(sqltype, 'character varying', 'varchar');
        elsif sqltype = 'timestamp without time zone' then
           sqltype_short := 'timestamp';
        end if;
        if lower(datatype) <> sqltype and lower(datatype) <> sqltype_short then
		i := 0;
		for v in
	        with recursive depv(relname, viewoid, depth) as (
		    select distinct a.relname, a.oid, 1
		        from pg_class a, pg_depend b, pg_depend c, pg_class d, pg_attribute e, pg_namespace
		        where a.oid = b.refobjid
			    and b.objid = c.objid
			    and b.refobjid <> c.refobjid
			    and c.refobjid = d.oid
			    and d.relname = lower(tablename)
			    and d.relkind in ('r','p')
			    and d.oid = e.attrelid
			    and e.attname = lower(columnname)
			    and c.refobjsubid = e.attnum
			    and a.relkind = 'v'
			    and a.relnamespace = pg_namespace.oid
			    and pg_namespace.nspname = namespace
	          union all
		    select distinct dependee.relname, dependee.oid, depv.depth+1
		        from pg_depend 
			    join pg_rewrite on pg_depend.objid = pg_rewrite.oid 
			    join pg_class as dependee on pg_rewrite.ev_class = dependee.oid 
			    join pg_class as dependent on pg_depend.refobjid = dependent.oid 
			    join pg_attribute ON pg_depend.refobjid = pg_attribute.attrelid and pg_depend.refobjsubid = pg_attribute.attnum and pg_attribute.attnum > 0
			    join depv on dependent.relname = depv.relname
	        )
	        select relname, viewoid, max(depth) from depv group by relname, viewoid order by 3 desc
		loop
		    raise notice 'view -> % %', v.relname, v.viewoid;
		    i := i + 1;
		    viewtext[i] := pg_get_viewdef(v.viewoid);
		    viewname[i] := v.relname;
		end loop;
		if i > 0 then
		   begin
		     for j in 1 .. i loop
		        SELECT String_agg('grant ' || privilege_type || ' on ' || viewname[j] || ' to "' || grantee || '"', '; ')
				into privs
				FROM information_schema.role_table_grants 
				WHERE table_name=viewname[j];
			    perms[j] := privs;
		        command := 'drop view ' || viewname[j];
			    raise notice 'executing -> %', command;
		        execute command;
		        dropviews[j] := viewname[j];
		     end loop;
                     exception
                        when others then
                          i := array_upper(dropviews, 1);
                          if i > 0 then
                             for j in reverse i .. 1 loop
                                command := 'create or replace view ' || dropviews[j] || ' as ' || viewtext[j];
			        raise notice 'executing -> %', 'create or replace view ' || dropviews[j] || '...';
		                execute command;
                             end loop;
                          end if;
                          raise exception 'Failed to recreate dependent view. SQLERRM=%', SQLERRM;
                   end;
		end if;
		command := 'alter table ' || lower(tablename) || ' alter column ' || lower(columnname) || ' type ' || lower(datatype);
		raise notice 'executing -> %', command;
		execute command;
                i := array_upper(dropviews, 1);
		if i > 0 then
		   for j in reverse i .. 1 loop
		     command := 'create or replace view ' || dropviews[j] || ' as ' || viewtext[j];
		     raise notice 'executing -> %', 'create or replace view ' || dropviews[j] || '...';
		     execute command;
		     command := perms[j];
		     raise notice 'executing -> %', command;
		     execute command;
		   end loop;
		end if;
        end if;
   end if;
   
   if defaultclause is not null then
       if lower(defaultclause) = 'null' then
          command := 'alter table ' || lower(tablename) || ' alter column ' || lower(columnname) || ' drop default ';
       else
          if defaultclause  ~ '.*[(].*[)].*' or lower(defaultclause) = 'current_timestamp' then
          	command := 'alter table ' || lower(tablename) || ' alter column ' || lower(columnname) || ' set default ' || defaultclause;
          else
	  	  	command := 'alter table ' || lower(tablename) || ' alter column ' || lower(columnname) || ' set default ''' || defaultclause || '''';
	  	  end if;
       end if;
       raise notice 'executing -> %', command;
       execute command;
   end if;
   
   if nullclause is not null then
      if lower(nullclause) = 'not null' then
          command := 'alter table ' || lower(tablename) || ' alter column ' || lower(columnname) || ' set not null';
          raise notice 'executing -> %', command;
          execute command;
      elsif lower(nullclause) = 'null' then
          command := 'alter table ' || lower(tablename) || ' alter column ' || lower(columnname) || ' drop not null';
          raise notice 'executing -> %', command;
          execute command;
      end if;
   end if;
end;
$$;


--
-- Name: anular_pagos_sin_cobro_legacy(date, numeric, numeric); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.anular_pagos_sin_cobro_legacy(p_fecha_trx date, p_ad_org_id numeric, p_c_doctype_id numeric) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
    -- Variables para el bucle principal de pagos
    v_payment RECORD;
    v_ad_table_id INTEGER;
    v_processed_count INTEGER := 0;
    
    -- Variables para la actualización de facturas
    v_all_invoice_ids NUMERIC(10,0)[] := '{}';
    v_unique_invoice_ids NUMERIC(10,0)[];
    v_current_invoice_id NUMERIC(10,0);
    v_open_amt NUMERIC;
    v_is_paid CHAR(1);

BEGIN
    -- Obtener el AD_Table_ID de C_Payment
    SELECT ad_table_id INTO v_ad_table_id
    FROM adempiere.ad_table
    WHERE tablename = 'C_Payment';

    IF NOT FOUND THEN
        RAISE EXCEPTION 'No se pudo encontrar el AD_Table_ID para la tabla C_Payment.';
    END IF;

    RAISE NOTICE 'Iniciando anulación de pagos para la fecha: %, Org: %, DocType: %', p_fecha_trx, p_ad_org_id, p_c_doctype_id;

    -- Bucle principal: Itera sobre cada pago a anular
    FOR v_payment IN
        SELECT pay.c_payment_id
        FROM adempiere.c_payment pay
        LEFT JOIN adempiere.legacy_cobro cob ON pay.c_payment_id = cob.local_id
        WHERE cob.local_id IS NULL
          AND pay.ad_org_id = p_ad_org_id
          AND pay.c_doctype_id = p_c_doctype_id
          AND pay.datetrx::DATE = p_fecha_trx::DATE
    LOOP
        RAISE NOTICE 'Procesando C_Payment_ID: %', v_payment.c_payment_id;

        -- Recolectar IDs de factura
        SELECT array_cat(v_all_invoice_ids, array_agg(c_invoice_id))
        INTO v_all_invoice_ids
        FROM adempiere.c_allocationline
        WHERE c_payment_id = v_payment.c_payment_id AND c_invoice_id IS NOT NULL;

        -- Actualizar pago, líneas de asignación y eliminar contabilización
        UPDATE adempiere.c_payment SET payamt = 0 WHERE c_payment_id = v_payment.c_payment_id;
        UPDATE adempiere.c_allocationline SET amount = 0 WHERE c_payment_id = v_payment.c_payment_id;
        DELETE FROM adempiere.fact_acct WHERE record_id = v_payment.c_payment_id AND ad_table_id = v_ad_table_id;

        v_processed_count := v_processed_count + 1;
    END LOOP;
    
    -- Obtener la lista de IDs únicos
    SELECT array_agg(DISTINCT id) INTO v_unique_invoice_ids FROM unnest(v_all_invoice_ids) AS id;

    -- Bucle secundario para actualizar las facturas
    RAISE NOTICE '----------------------------------------------------------';
    RAISE NOTICE 'Actualizando saldos de % facturas afectadas...', COALESCE(array_length(v_unique_invoice_ids, 1), 0);

    IF v_unique_invoice_ids IS NOT NULL THEN
        FOREACH v_current_invoice_id IN ARRAY v_unique_invoice_ids
        LOOP
            -- Calcular el saldo abierto usando la función correcta
            v_open_amt := adempiere.invoiceopen(v_current_invoice_id, NULL);
            
            -- Determinar si la factura está pagada o no, basándose en el saldo
            v_is_paid := CASE WHEN v_open_amt <= 0 THEN 'Y' ELSE 'N' END;
            
            -- ====================================================================
            -- >> LÓGICA DE ACTUALIZACIÓN CORREGIDA
            -- Se eliminan las columnas 'openamt' y 'paidamt' que no existen.
            -- Solo se actualiza 'ispaid' y 'updated'.
            -- ====================================================================
            UPDATE adempiere.c_invoice
            SET ispaid = v_is_paid,
                updated = NOW()
            WHERE c_invoice_id = v_current_invoice_id;

            RAISE NOTICE '  -> Factura ID: % actualizada. Nuevo estado IsPaid: % (Saldo Abierto: %)', v_current_invoice_id, v_is_paid, v_open_amt;
        END LOOP;
    END IF;

    RAISE NOTICE 'Proceso completado. Total de pagos anulados: %', v_processed_count;
    RETURN v_processed_count;
END;
$$;


--
-- Name: anular_pagos_sin_cobro_legacy_fecha(numeric, timestamp without time zone); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.anular_pagos_sin_cobro_legacy_fecha(p_c_doctype_id numeric, p_fecha_operacion timestamp without time zone) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
    -- Variable para el bucle de organizaciones (ahora incluye el nombre)
    v_org RECORD;
    v_processed_orgs_count INTEGER := 0;
    v_pagos_anulados_total INTEGER := 0;
    v_pagos_anulados_en_org INTEGER := 0;

BEGIN
    RAISE NOTICE '======================================================================';
    RAISE NOTICE 'INICIO DE PROCESO MASIVO DE ANULACIÓN PARA FECHA: %', p_fecha_operacion::DATE;
    RAISE NOTICE 'Buscando organizaciones con movimientos en legacy_cobro...';
    RAISE NOTICE '======================================================================';

    -- ====================================================================
    -- >> CAMBIO: El bucle ahora une con AD_Org para obtener el nombre.
    -- Itera sobre cada organización que tuvo operaciones en la fecha.
    -- ====================================================================
    FOR v_org IN
        SELECT DISTINCT
            lc.ad_org_id,
            org.name
        FROM
            adempiere.legacy_cobro lc
            JOIN adempiere.ad_org org ON lc.ad_org_id = org.ad_org_id
        WHERE
            lc.operacion::DATE = p_fecha_operacion::DATE
        ORDER BY
            lc.ad_org_id
    LOOP
        v_processed_orgs_count := v_processed_orgs_count + 1;
        
        -- ====================================================================
        -- >> CAMBIO: El mensaje ahora incluye el nombre de la organización.
        -- ====================================================================
        RAISE NOTICE '--- Procesando Organización: % (%) ---', v_org.name, v_org.ad_org_id;

        -- Invocamos a la función detallada que ya hemos creado y probado.
        SELECT adempiere.anular_pagos_sin_cobro_legacy(
            p_fecha_operacion::DATE, -- La fecha de la operación
            v_org.ad_org_id,         -- La organización actual del bucle
            p_c_doctype_id           -- El tipo de documento recibido como parámetro
        ) INTO v_pagos_anulados_en_org;

        v_pagos_anulados_total := v_pagos_anulados_total + v_pagos_anulados_en_org;
        RAISE NOTICE '--- Fin de procesamiento para Org: %. Pagos anulados: % ---', v_org.name, v_pagos_anulados_en_org;
        RAISE NOTICE ' '; -- Línea en blanco para legibilidad

    END LOOP;

    RAISE NOTICE '======================================================================';
    RAISE NOTICE 'FIN DE PROCESO MASIVO.';
    RAISE NOTICE 'Organizaciones procesadas: %', v_processed_orgs_count;
    RAISE NOTICE 'Total de pagos anulados en todas las organizaciones: %', v_pagos_anulados_total;
    RAISE NOTICE '======================================================================';

    -- Retorna el número total de organizaciones procesadas
    RETURN v_processed_orgs_count;
END;
$$;


--
-- Name: bompricelimit(numeric, numeric); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.bompricelimit(product_id numeric, pricelist_version_id numeric) RETURNS numeric
    LANGUAGE plpgsql STABLE
    AS $$
DECLARE
	v_Price	NUMERIC;
	v_ProductPrice	NUMERIC;
	bom RECORD;

BEGIN
	--	Try to get price from PriceList directly
	SELECT	COALESCE (SUM(PriceLimit), 0)
      	INTO	v_Price
   	FROM	M_ProductPrice
	WHERE IsActive='Y' AND M_PriceList_Version_ID=PriceList_Version_ID AND M_Product_ID=Product_ID;

	--	No Price - Check if BOM
	IF (v_Price = 0) THEN
		FOR bom IN  
			SELECT b.M_ProductBOM_ID, b.BOMQty, p.IsBOM
			FROM M_Product_BOM b, M_Product p
			WHERE b.M_ProductBOM_ID=p.M_Product_ID
			AND b.M_Product_ID=Product_ID
			AND b.M_ProductBOM_ID != Product_ID
			AND (p.IsBOM='N' OR p.IsVerified='Y')
			AND b.IsActive='Y'
		LOOP
			v_ProductPrice := bomPriceLimit (bom.M_ProductBOM_ID, PriceList_Version_ID);
			v_Price := v_Price + (bom.BOMQty * v_ProductPrice);
		END LOOP;
	END IF;
	--
	RETURN v_Price;
	
END;
$$;


