--
-- Name: caf_is_period_open(numeric, timestamp without time zone); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.caf_is_period_open(dtid numeric, docdate timestamp without time zone) RETURNS boolean
    LANGUAGE plpgsql
    AS $$
DECLARE
	doctype record;
	period record;
	pc record;
BEGIN
	-- obtenemos instancia del doctype
	select dt.* from c_doctype dt where dt.c_doctype_id = dtid into doctype;
	
	-- Obtenemos cuál es el periodo para la fecha
	select p.* from c_period p where docdate between p.startdate and p.enddate limit 1 into period;

	-- Obtenemos period control para esa fecha y ese
	select ppcc.* from C_PeriodControl ppcc where ppcc.docbasetype = doctype.docbasetype and C_Period_ID = period.C_Period_ID into pc;

	-- check if is active and open
	-- raise notice 'Period: % Status: %', period.name, pc.periodstatus;
	return (pc.periodstatus = 'O');
END
$$;


