--
-- Name: crawl_tree(integer, integer, integer); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.crawl_tree(integer, integer, integer) RETURNS SETOF adempiere.node_relationships_n_level
    LANGUAGE plpgsql
    AS $_$
DECLARE
    tempo RECORD;
    child RECORD;
BEGIN
    SELECT INTO tempo ad_tree_id, node_id, ad_client_id, ad_org_id, isactive, created, createdby, updated, updatedby, parent_id, seqno, $3 AS level
    FROM adempiere.ad_treenode
    WHERE adempiere.ad_treenode.ad_tree_id = $1 AND node_id = $2;

    IF FOUND THEN 
        RETURN NEXT tempo;
        
        FOR child IN 
            SELECT node_id 
            FROM adempiere.ad_treenode 
            WHERE ad_tree_id = $1 AND parent_id = $2 
            ORDER BY seqno
        LOOP
            FOR tempo IN 
                SELECT * FROM crawl_tree($1, cast(child.node_id as INTEGER), $3 + 1) 
            LOOP
                RETURN NEXT tempo;
            END LOOP;
        END LOOP;
    END IF;
    
    RETURN;
END;
$_$;


