--
-- Name: documentno(numeric); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.documentno(p_pp_mrp_id numeric) RETURNS character varying
    LANGUAGE plpgsql STABLE
    AS $$
DECLARE
	v_DocumentNo PP_MRP.Value%TYPE := '';
BEGIN
	-- If NO id return empty string
	IF p_PP_MRP_ID <= 0 THEN
		RETURN '';
	END IF;
	SELECT --ordertype, m_forecast_id, c_order_id, dd_order_id, pp_order_id, m_requisition_id,
	CASE
			WHEN trim(mrp.ordertype) = 'FTC' THEN (SELECT f.Name FROM M_Forecast f WHERE f.M_Forecast_ID=mrp.M_Forecast_ID)
			WHEN trim(mrp.ordertype) = 'POO' THEN (SELECT co.DocumentNo  FROM C_Order co WHERE co.C_Order_ID=mrp.C_Order_ID)
			WHEN trim(mrp.ordertype) = 'DOO' THEN (SELECT dd.DocumentNo  FROM DD_Order dd WHERE dd.DD_Order_ID=mrp.DD_Order_ID)
			WHEN trim(mrp.ordertype) = 'SOO' THEN (SELECT co.DocumentNo  FROM C_Order co WHERE co.C_Order_ID=mrp.C_Order_ID)
			WHEN trim(mrp.ordertype) = 'MOP' THEN (SELECT po.DocumentNo FROM PP_Order po WHERE po.PP_Order_ID=mrp.PP_Order_ID)
			WHEN trim(mrp.ordertype) = 'POR' THEN (SELECT r.DocumentNo  FROM M_Requisition r WHERE r.M_Requisition_ID=mrp.M_Requisition_ID)
			
	END INTO v_DocumentNo
	FROM pp_mrp mrp
	WHERE mrp.pp_mrp_id = p_PP_MRP_ID;
	RETURN v_DocumentNo;
END;	
$$;


--
-- Name: duho_autofix_bphist(numeric); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.duho_autofix_bphist(bpid numeric) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
	topcredit record;
	oldcredit record;
	cobro record;
	saldo_to_fix numeric default 0;
	topcredit_abonado numeric default 0;
	saldopendiente numeric default 0;
BEGIN

select *
from legacy_cartera
where saldo>0
and c_bpartner_id = bpid
order by fecha desc limit 1
into topcredit;

select *
from legacy_cartera
where saldo>0
and c_bpartner_id = bpid
order by fecha desc limit 1 offset 1
into oldcredit;

select sum(abono) from legacy_cobro where id_cartera = topcredit.legacy_cartera_id into topcredit_abonado;

if(topcredit_abonado>=oldcredit.saldo) then
	raise notice '[BPID: %] Crédito % - % puede ser arreglado [Av: % <- %]', bpid, oldcredit.legacy_cartera_id, oldcredit.saldo, topcredit_abonado, topcredit.legacy_cartera_id;
	saldopendiente := oldcredit.saldo;
	for cobro in select * from legacy_cobro where id_cartera = topcredit.legacy_cartera_id order by operacion asc loop
		if(saldopendiente>=cobro.abono) then
			saldopendiente := saldopendiente - cobro.abono;
			-- cartera of cobro should be updated
			update legacy_cobro set id_cartera = oldcredit.legacy_cartera_id where legacy_cobro_id = cobro.legacy_cobro_id;
			raise notice 'Abono % :: updated with new cartera id :: Saldo Pendiente :: % ', cobro.legacy_cobro_id, saldopendiente;
		end if;
	end loop;
	perform duho_updatesaldo_cartera(oldcredit.legacy_cartera_id);
	perform duho_updatesaldo_cartera(topcredit.legacy_cartera_id);
	return 1;
else
	raise notice 'Abonado a crédito nuevo % no cubre Saldo Crédito Viejo %', topcredit_abonado, oldcredit.saldo;
	return 0;
end if;
END
$$;


--
-- Name: duho_balancecheck(); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.duho_balancecheck() RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE 
 bptofix record;
BEGIN   
for bptofix in 
with bptocheck as (
	select bp.name, bp.c_bpartner_id,
(select coalesce(sum(montototal),0.00) from legacy_cartera carint where carint.c_bpartner_id = bp.c_bpartner_id )
-
(select coalesce(sum(abono),0.00) from legacy_cobro cobint where cobint.c_bpartner_id = bp.c_bpartner_id )
as saldo,
bp.totalopenbalance
from c_bpartner bp
where exists (select 1 from legacy_cartera lgc where lgc.c_bpartner_id = bp.c_bpartner_id)
	)
select * from bptocheck bpt
where bpt.saldo<>bpt.totalopenbalance loop
	-- raise notice 'BP % should be fixed with saldo: % ', bptofix.name, bptofix.saldo;
	update c_bpartner set totalopenbalance = bptofix.saldo, updated = now()
	where c_bpartner_id = bptofix.c_bpartner_id;
end loop;
END;
$$;


--
-- Name: duho_change_cartera_data(numeric, numeric, numeric, numeric); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.duho_change_cartera_data(carteraid numeric, nuevomonto numeric, nuevointeres numeric, nuevoplazo numeric) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
	nuevacuota numeric default 0.00;
	nuevomontototal numeric default 0.00;
	nuevovalorinteres numeric default 0.00;
	cartera record;
	invoice record;
BEGIN
	nuevovalorinteres := round(nuevomonto * nuevointeres,2);
	nuevomontototal := round( nuevomonto + (nuevomonto * nuevointeres), 2 );
	nuevacuota := round( nuevomontototal / nuevoplazo, 2);
	
	select * from legacy_cartera where legacy_cartera_id = carteraid into cartera;
	if(cartera.legacy_cartera_id is not null) then
		raise notice 'Cartera ::=> % | Monto: %, Cuota: %, Interés: %, Total: %', cartera, nuevomonto, nuevacuota, nuevovalorinteres, nuevomontototal;
		-- select * from legacy_cartera where legacy_cartera_id = 10347012
		update legacy_cartera 
			set dias_cre = nuevoplazo, cuota = nuevacuota, 
			monto = nuevomonto, valorinteres = nuevovalorinteres,
			montototal = nuevomontototal, tasa = nuevointeres,
			minilog = coalesce(minilog,'') || '\nUpdated with amt: ' || nuevomonto::character varying
			where legacy_cartera_id = carteraid;
		if(cartera.local_id is not null) then
			select * from c_invoice where c_invoice_id = cartera.local_id into invoice;
			if(invoice.c_invoice_id is not null) then
				-- select * from c_invoiceline where c_invoice_id = 1027383
				-- select * from c_invoice where c_invoice_id = 1027383
				update c_invoiceline set priceactual = nuevomonto, priceentered = nuevomonto, linenetamt = nuevomonto where c_charge_id = 1000028 and c_invoice_id = invoice.c_invoice_id;
				update c_invoiceline set priceactual = nuevovalorinteres, priceentered = nuevovalorinteres, linenetamt = nuevovalorinteres where c_charge_id = 1000029 and c_invoice_id = invoice.c_invoice_id;
				update c_invoice set totallines = nuevomontototal, grandtotal = nuevomontototal, posted = 'N' where c_invoice_id = invoice.c_invoice_id;
				
				-- Actualizar desembolso
				update c_payment 
					set payamt = nuevomonto
					where c_payment_id = cartera.payment_id;  
				perform duho_updatesaldo_cartera(carteraid);
				return 1;
			end if;
		end if;
	end if;
	return 0;
END
$$;


--
-- Name: duho_fix_c_payment_desembolsos_huerfanos(date); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.duho_fix_c_payment_desembolsos_huerfanos(fecha_proceso date) RETURNS void
    LANGUAGE plpgsql
    AS $$
BEGIN
    UPDATE adempiere.c_payment
    SET payamt = 0
    WHERE c_payment_id IN (
        SELECT pay.c_payment_id
        FROM adempiere.c_payment pay
        LEFT JOIN adempiere.legacy_cartera car ON pay.c_payment_id = car.payment_id
        LEFT JOIN adempiere.c_allocationline line ON pay.c_payment_id = line.c_payment_id
