--

CREATE FUNCTION adempiere.duho_updatesaldo(bpid numeric) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE 
 balance numeric default 0.00;
 debitos numeric default 0.00;
 creditos numeric default 0.00;
BEGIN   
	debitos := (select coalesce(sum(montototal),0.00) from legacy_cartera where c_bpartner_id = bpid );
	creditos := (select coalesce(sum(abono),0.00) from legacy_cobro where c_bpartner_id = bpid);
	balance := debitos - creditos;
