--
-- Name: duho_updatesaldo_cartera(numeric); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.duho_updatesaldo_cartera(lgcid numeric) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE 
 cartera record;
 balance numeric default 0.00;
 debitos numeric default 0.00;
 creditos numeric default 0.00;
 fechavence date;
 BEGIN  
	select * from legacy_cartera where legacy_cartera_id = lgcid into cartera;
	select (cartera.fecha::timestamp with time zone + cartera.dias_cre::numeric)::date into fechavence;
	debitos := (select coalesce(sum(montototal),0.00) from legacy_cartera where legacy_cartera_id = lgcid );
	creditos := (select coalesce(sum(abono),0.00) from legacy_cobro 
				 where ad_org_id = cartera.ad_org_id and id_cartera = lgcid );
	balance := debitos - creditos;
	if(balance>0 and now()::date > fechavence) then
		update c_bpartner
		set duns = 'KO'
		where c_bpartner_id = cartera.c_bpartner_id;
	else
		update c_bpartner
		set duns = 'OK'
		where c_bpartner_id = cartera.c_bpartner_id;
	end if;
	update legacy_cartera 
		set saldo = balance, updated = now() 
		where legacy_cartera_id = lgcid;
		raise notice '% :===> % ', lgcid, balance; 
	return;
END;
$$;


