			from	cuentas c
				left join adempiere.AD_TreeNode tn    on c.parent_id=tn.Node_ID
				left join adempiere.C_ElementValue ev on  tn.Node_ID=ev.C_ElementValue_ID
				left join adempiere.C_ElementValue ev2 on  tn.parent_id=ev2.C_ElementValue_ID
			where 	tn.AD_Client_ID= $1

		) 
		select _index, 
			Node_ID,
			cod_cuenta,
			cuenta,
			accounttype,
			parent_id,
			cod_cuentapadre,
			cuentapadre, 
			case 	when  cs.accounttype='E'  or cs.accounttype='C'
			then 
				coalesce(sum
				(
					/*case fa.C_Currency_ID when 100 
						then 	fa.AmtSourceDr
						else*/
							fa.AmtAcctDr
					--end 
				),0) - 
				coalesce(sum
				(
					/*case fa.C_Currency_ID when 100 
						then 	fa.AmtSourceCr
						else*/
							fa.AmtAcctCr
					--end 
				),0)  
			else
				

				coalesce(sum
				(
					/*case fa.C_Currency_ID when 100 
						then 	fa.AmtSourceCr
						else*/
							fa.AmtAcctCr
					--end 
				),0)
				- 
				coalesce(sum
				(
					/*case fa.C_Currency_ID when 100 
						then 	fa.AmtSourceDr
						else*/
							fa.AmtAcctDr
					--end 
				),0)   
			end acumulado,
			0 actual
		from	cuentas cs
			left join adempiere.Fact_Acct fa on fa.account_id=cs.Node_ID
		where 	fa.AD_Client_ID=$1
			and fa.dateacct::date<=	(
						select enddate::date
