						where  C_Period_ID=$2 and isActive='Y'
						)

			    and fa.C_Period_ID not in
			    (
				 select
				     case    when
					    (select ((((select trim(fiscalyear) from adempiere.C_Year where C_Year.C_Year_ID= $4 )||'1201')::TIMESTAMP) + INTERVAL '1 MONTH - 1 day')::DATE )
					    =(
						select enddate::date
						    from adempiere.C_Period p
						    where  C_Period_ID= $2   and isActive='Y'
					    )
					    then
						case when $5::boolean  --PARAMETRO CON AJUSTE O SIN AJUSTE
						    then
							-1
						     else
						    (
							select  C_Period_ID
							from     adempiere.C_Period p
							    inner join adempiere.C_Year y on p.C_Year_ID= y.C_Year_ID
							where    y.fiscalyear::text=
							    (
								select fiscalyear::text
								from adempiere.C_Period p
								inner join adempiere.C_Year y on p.C_Year_ID= y.C_Year_ID
								where  p.C_Period_ID= $2   and p.isActive='Y'
							    ) and p.es_cierre_contable='Y'
						    )
						end
					else
					    -1
				    end
			    )

			and (cs.accounttype='R' or cs.accounttype='E' or cs.accounttype='C') 	
			and  Node_ID<>0
		group by _index, 
			Node_ID,
			cod_cuenta,
			cuenta,
			accounttype,
			parent_id,
			cod_cuentapadre,
			cuentapadre

		union all 

		select _index, 
