						then 	fa.AmtSourceCr
						else*/
							fa.AmtAcctCr
					--end 
				),0)  
			else
				

				coalesce(sum
				(
					/*case fa.C_Currency_ID when 100 
						then 	fa.AmtSourceCr
						else*/
							fa.AmtAcctCr
					--end 
				),0)
				- 
				coalesce(sum
				(
					/*case fa.C_Currency_ID when 100 
						then 	fa.AmtSourceDr
						else*/
							fa.AmtAcctDr
					--end 
				),0)   
			end actual
			 
		from	cuentas cs
			left join adempiere.Fact_Acct fa on fa.account_id=cs.Node_ID and fa.C_Period_ID=$2  and fa.AD_Client_ID=$1
		where 	(cs.accounttype='R' or cs.accounttype='E' or cs.accounttype='C') 
