		group by _index, 
			Node_ID,
			cod_cuenta,
			cuenta,
			accounttype,
			parent_id,
			cod_cuentapadre,
			cuentapadre
		order by cod_cuenta
	),sumapadre
	as
	(
		with sumaglobal 
		as
		(
			with recursive suma
			as(
				select  _index,
					arbol.Node_ID,
					arbol.parent_id, 
					(arbol.acumulado), 
					(arbol.actual) 
					
				from 	arbol  
				where 	_index=1  

				union all 

				select  arbol._index,
					arbol.Node_ID,
					arbol.parent_id, 
					s.acumulado,
					s.actual 
					
				from suma  s
				inner join arbol on s.parent_id=arbol.node_id 
			)select * from suma
		)
		select 	_index,
			Node_ID,	
			parent_id, 
			coalesce(sum(acumulado),0)acumulado,
			coalesce(sum(actual),0) actual 
		from 	sumaglobal sm
			inner join  adempiere.C_ElementValue ev on  sm.Node_ID=ev.C_ElementValue_ID
		group by Node_ID,_index,parent_id
		having _index<>1 
	),
	definirniveles
	as(	
		with unirniveles
		as(
			select  sum(case a._index  when 11111 then 1 else 0 end) over(order by cod_cuenta) grouporaiz,
				a._index,
				a.Node_ID,
				a.parent_id,
				cod_cuenta,
				a.cuenta, 
				case a._index
					when 1  then a.acumulado
					else  sp.acumulado
				end acumulado,	  
				(case coalesce(a.actual,0) when 0 then coalesce(sp.actual,0) else coalesce(a.actual,0) end) actual 
				
				 
			from 	arbol a
				left join sumapadre sp on a.Node_ID=sp.Node_ID 
			where 	a._index>=  $3
			order by cod_cuenta
		)
		select  grouporaiz,
			first_value(cuenta) over(partition by grouporaiz) grouporaiz2,
			_index,  
			cod_cuenta,
			cuenta, 
			round(acumulado,2) acumulado ,	  
			round(actual,2) actual,
			first_value(acumulado) over(partition by grouporaiz) acumuladoagrupado,
			first_value(actual) over(partition by grouporaiz) actualagrupado
		from unirniveles 
		
	) ,ubuei
	as
	(
		 

		select  DISTINCT 1.5 grouporaiz,
			'UTILIDADOPERDIDABRUTA' grouporaiz2,
			11111 _index,  
			'' cod_cuenta,
			'UTILIDAD O PERDIDA BRUTA' cuenta, 
			SUM((CASE WHEN grouporaiz2='INGRESOS' THEN acumuladoagrupado ELSE 0 END ) -
			(CASE WHEN grouporaiz2='COSTOS' THEN acumuladoagrupado ELSE 0 END)) OVER () acumulado,
			SUM((CASE WHEN grouporaiz2='INGRESOS' THEN actualagrupado ELSE 0 END ) -
			(CASE WHEN grouporaiz2='COSTOS' THEN actualagrupado ELSE 0 END)) OVER () actual 		
		from definirniveles
		where (grouporaiz2='INGRESOS' OR grouporaiz2='COSTOS') AND _index=11111 
		 

		UNION ALL 

		select  DISTINCT 4 grouporaiz,
			'UTILIDADOPERDIDADELEJERCICIO' grouporaiz2,
			11111 _index,  
			'' cod_cuenta ,
			'UTILIDAD O PERDIDA DEL EJERCICIO' cuenta, 
			SUM(
				(CASE WHEN grouporaiz2='INGRESOS' THEN acumuladoagrupado ELSE 0 END ) -
				(CASE WHEN grouporaiz2='COSTOS' THEN acumuladoagrupado ELSE 0 END) -
				(CASE WHEN grouporaiz2='GASTOS' THEN acumuladoagrupado ELSE 0 END)
			) OVER () acumulado,
			SUM(
				(CASE WHEN grouporaiz2='INGRESOS' THEN actualagrupado ELSE 0 END ) -
				(CASE WHEN grouporaiz2='COSTOS' THEN actualagrupado ELSE 0 END) -
				(CASE WHEN grouporaiz2='GASTOS' THEN actualagrupado ELSE 0 END)
			) OVER () actual 		
		from definirniveles
		where _index=11111 

		union all

		select  DISTINCT 5 grouporaiz,
			'IMPUESTOSOBRERENTA' grouporaiz2,
			11111 _index,  
			ev.value cod_cuenta ,
			upper(ev.name) cuenta,  
			(
			coalesce(	sum
					(
						/*case fa.C_Currency_ID when 100 
							then 	fa.AmtSourceCr
							else*/
								fa.AmtAcctCr
						--end 
					) over (),0
				) 
				- 
			coalesce
				(
					sum
					(
						/*case fa.C_Currency_ID when 100 
							then 	fa.AmtSourceDr
							else*/
								fa.AmtAcctDr
						--end 
					)over (),0
				)   
			) acumulado,
			case 	when fa.C_Period_ID=  $2
					then 	(
							coalesce(	sum
									(
										/*case fa.C_Currency_ID when 100 
											then 	fa.AmtSourceCr
											else*/
												fa.AmtAcctCr
										--end 
									) over (),0
								) 
								- 
							coalesce
								(
									sum
									(
										/*case fa.C_Currency_ID when 100 
											then 	fa.AmtSourceDr
											else*/
												fa.AmtAcctDr
										--end 
									)over (),0
								)   
						)
						
				else 0 
			end actual
		FROM 	adempiere.Fact_Acct fa 
			right JOIN  adempiere.C_ElementValue ev on fa.account_id=ev.C_ElementValue_ID
		where 	ev.isdoccontrolled='Y' and ev.ad_client_id= $1
		
	)  
	SELECT 	grouporaiz,
		grouporaiz2,
		_index,  
		cod_cuenta,
		cuenta, 
		acumulado ,	  
		actual
	FROM	definirniveles
	where 	_index<>1 or (_index=1 and 1=(
								case 	when  (acumulado<>0 and actual<>0) or (acumulado=0 and actual<>0) or (acumulado<>0 and actual=0)
										then 1
									else
										0
								end
						))

	UNION ALL 

	SELECT 	*
	FROM	ubuei

	UNION ALL

	select  DISTINCT 8 grouporaiz,
		'UTILIDADNETAPERIODO' grouporaiz2,
		11111 _index,  
		'' cod_cuenta,
		'UTILIDAD NETA DEL PERIODO' cuenta ,
		sum( (CASE WHEN grouporaiz=4 THEN acumulado  ELSE 0 END ) - (CASE WHEN grouporaiz=5 THEN acumulado ELSE 0 END) ) over() acumulado,
		sum((CASE WHEN grouporaiz=4 THEN actual  ELSE 0 END ) - (CASE WHEN grouporaiz=5 THEN actual ELSE 0 END)) over() actual
	from ubuei
	where grouporaiz =4 or grouporaiz=5
	order by grouporaiz,cod_cuenta;

end;
$_$;


--
-- Name: firstof(timestamp with time zone, character varying); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.firstof(timestamp with time zone, character varying) RETURNS date
    LANGUAGE plpgsql IMMUTABLE
    AS $_$
DECLARE
datepart VARCHAR;
datetime TIMESTAMP WITH TIME ZONE;
offsetdays INTEGER;
BEGIN
	datepart = $2;
	offsetdays = 0;
	IF $2 IN ('') THEN
		datepart = 'millennium';
	ELSEIF $2 IN ('') THEN
		datepart = 'century';
	ELSEIF $2 IN ('') THEN
		datepart = 'decade';
	ELSEIF $2 IN ('IYYY','IY','I') THEN
		datepart = 'year';
	ELSEIF $2 IN ('SYYYY','YYYY','YEAR','SYEAR','YYY','YY','Y') THEN
		datepart = 'year';
	ELSEIF $2 IN ('Q') THEN
		datepart = 'quarter';
	ELSEIF $2 IN ('MONTH','MON','MM','RM') THEN
		datepart = 'month';
	ELSEIF $2 IN ('IW') THEN
		datepart = 'week';
	ELSEIF $2 IN ('W') THEN
		datepart = 'week';
	ELSEIF $2 IN ('DDD','DD','J') THEN
		datepart = 'day';
	ELSEIF $2 IN ('DAY','DY','D') THEN
		datepart = 'week';
		-- move to sunday to make it compatible with oracle and SQLJ
		offsetdays = -1;
	ELSEIF $2 IN ('HH','HH12','HH24') THEN
		datepart = 'hour';
	ELSEIF $2 IN ('MI') THEN
		datepart = 'minute';
	ELSEIF $2 IN ('') THEN
		datepart = 'second';
	ELSEIF $2 IN ('') THEN
		datepart = 'milliseconds';
	ELSEIF $2 IN ('') THEN
		datepart = 'microseconds';
	END IF;
	datetime = date_trunc(datepart, $1); 
RETURN cast(datetime as date) + offsetdays;
END;
$_$;


