        WHERE  ad_tree_id      = treeid
               AND node_id     = idcuenta
    );
    idcuenta_aux = parentid;
    IF  (parentid=0)
    THEN
        RETURN nivelcuenta;
    END IF;
   
    WHILE parentid > 0
    LOOP
        parentid =(
            SELECT parent_id
            FROM   adempiere.ad_treenode
            WHERE  ad_tree_id      = treeid
                   AND node_id     = idcuenta_aux
        );
        idcuenta_aux = parentid;
        nivelcuenta = nivelcuenta + 1;
    END LOOP;
   
    RETURN nivelcuenta;
END;
$$;


--
-- Name: fn_usr_getparentid_upto(numeric, numeric); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.fn_usr_getparentid_upto(idcuenta numeric, nivelparent numeric) RETURNS numeric
    LANGUAGE plpgsql
    AS $$
        DECLARE
        nivelcuentaactual NUMERIC;

    nivelcuentapadre NUMERIC;
    parentid NUMERIC;
    parentid_aux NUMERIC;
BEGIN
    nivelcuentaactual =(
        SELECT nivelcuenta
        FROM   usr_v_cuentas_catalogo
        WHERE  c_elementvalue_id = idcuenta
    );
    IF  (nivelcuentaactual<=1)
    THEN
        RETURN idcuenta;
    END IF;
   
    parentid =(
        SELECT parent_id
        FROM   usr_v_cuentas_catalogo
        WHERE  c_elementvalue_id = idcuenta
    );
    nivelcuentapadre =(
        SELECT nivelcuenta
        FROM   usr_v_cuentas_catalogo
        WHERE  c_elementvalue_id = parentid
    );
    IF  (nivelcuentapadre<=nivelparent)
    THEN
        RETURN parentid;
    END IF;
   
    WHILE nivelcuentapadre<> nivelparent
    LOOP
        parentid_aux = parentid;
        parentid =(
            SELECT parent_id
            FROM   usr_v_cuentas_catalogo
            WHERE  c_elementvalue_id = parentid_aux
        );
        nivelcuentapadre =(
            SELECT nivelcuenta
            FROM   usr_v_cuentas_catalogo
            WHERE  c_elementvalue_id = parentid
        );
    END LOOP;
   
    RETURN parentid;
END;
$$;


