	    	WHEN p.periodtype='A' THEN 'Y'
	    	WHEN p.periodtype='S' THEN 'N'
	    	ELSE 'N'
	    END  AS es_cierre_contable
	    
	    
	    
	    
	    FROM adempiere.c_period AS p
