		        			WHEN (
		        			     	COALESCE(SUM(fac.amtacctdr-fac.amtacctcr), v_defaultnumber)
		        			     )
		        			     > v_defaultnumber THEN (
		        			     	SELECT
		        			     		COALESCE(SUM(fac.amtacctdr-fac.amtacctcr), v_defaultnumber)
		        			     )
		        			ELSE v_defaultnumber
		        		END              AS inicialdr,
		        		CASE 
		        			WHEN (
		        			     	COALESCE(SUM(fac.amtacctdr-fac.amtacctcr), v_defaultnumber)
		        			     )
		        			     <
		        			     v_defaultnumber THEN -(
		        			     	SELECT
		        			     		COALESCE(SUM(fac.amtacctdr-fac.amtacctcr), v_defaultnumber)
		        			     )
		        			ELSE v_defaultnumber
