--
-- Name: fnt_docstatus(character); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.fnt_docstatus(p_doc_status_id character) RETURNS character varying
    LANGUAGE plpgsql
    AS $$
	DECLARE
	v_name_status CHARACTER VARYING (20) ;
BEGIN
	SELECT
		CASE 
			WHEN p_doc_status_id = 'DR' THEN 'Borrador'
			WHEN p_doc_status_id = 'VO' THEN 'Anulado'
			WHEN p_doc_status_id = 'CL' THEN 'Cerrado'
			WHEN p_doc_status_id = 'CO' THEN 'Completo'
			WHEN p_doc_status_id = '??' THEN 'Desconocido'
			WHEN p_doc_status_id = 'IP' THEN 'En Proceso'
			WHEN p_doc_status_id = 'WC' THEN 'Esperando Confirmacion'
			WHEN p_doc_status_id = 'WP' THEN 'Esperando Pago'
			WHEN p_doc_status_id = 'NA' THEN 'No Aprobado'
			WHEN p_doc_status_id = 'IN' THEN 'No Valido'
			WHEN p_doc_status_id = 'RE' THEN 'Reversado'
			ELSE '*******'
		END 
	INTO
		v_name_status; 
	
	RETURN UPPER (v_name_status) :: CHARACTER VARYING (20);
END;
$$;


