-- Name: fnt_estado_contable_nota(numeric); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.fnt_estado_contable_nota(p_gl_journal_id numeric) RETURNS character varying
    LANGUAGE plpgsql
    AS $$

DECLARE
_cantidad INTEGER := 0;
_status CHARACTER VARYING := '*';
_isposted CHARACTER VARYING := '*';
_message_status_accounting CHARACTER VARYING;
BEGIN
SELECT
	gl.docstatus,
	CASE COALESCE(
	     	(
	     		SELECT
	     			COUNT (*)
	     		FROM
	     			adempiere.fact_acct
	     		WHERE
	     			ad_table_id          = 224
	     			AND ad_client_id     = 1000000
	     			AND record_id        = gl.gl_journal_id
	     	),
	     	0
	     )
		WHEN 0 THEN 'N'
		ELSE 'Y'
	END                   AS fueposteado
FROM
	adempiere.gl_journal     gl
WHERE
	gl.gl_journal_id = p_gl_journal_id

	INTO _status,_isposted;


IF  (_status = 'CO' AND _isposted = 'Y')
THEN
	_message_status_accounting := UPPER ('Contabilizado');
ELSIF (_status = 'CO' AND _isposted = 'N')
THEN
	_message_status_accounting := UPPER ('Contzn Pendiente');
ELSIF (
      	_status  IN ('DR', 'VO', 'AP', 'CL', '??', 'IP', 'WC', 'WP', 'NA',
      	            'IN', 'RE')
      	AND _isposted = 'N'
      )
THEN
	_message_status_accounting := UPPER ('Contzn Pendiente');
ELSIF (
      	_status  IN ('DR', 'VO', 'AP', 'CL', '??', 'IP', 'WC', 'WP', 'NA',
      	            'IN', 'RE')
      	AND _isposted = 'Y'
      )
THEN
	_message_status_accounting := UPPER ('Contzn Con Errores');
END IF;


RETURN _message_status_accounting;
END;

$$;


