--
-- Name: get_customer_payments(numeric); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.get_customer_payments(period_id numeric) RETURNS TABLE(organizacion character varying, ruta character varying, c_bpartner_id integer, legacy_cartera_id numeric, fecha date, nombrecliente character varying, montobase numeric, interes numeric, saldoinicial numeric, date1 numeric, date2 numeric, date3 numeric, date4 numeric, date5 numeric, date6 numeric, date7 numeric, date8 numeric, date9 numeric, date10 numeric, date11 numeric, date12 numeric, date13 numeric, date14 numeric, date15 numeric, date16 numeric, date17 numeric, date18 numeric, date19 numeric, date20 numeric, date21 numeric, date22 numeric, date23 numeric, date24 numeric, date25 numeric, date26 numeric, date27 numeric, date28 numeric, date29 numeric, date30 numeric, date31 numeric, totalmovs numeric, saldofinal numeric)
    LANGUAGE plpgsql
    AS $$
DECLARE
  bprec RECORD;
  periodo RECORD;
  dates_array date[];
  cobros_array numeric[];
  cobro_value numeric;
  creditos_array numeric[];
  credito_value numeric;
  neto numeric;
  totalCobros numeric;
  totalCreditos numeric;
  carterarec RECORD;
  initialDate date;
  saldoFinalValor numeric;
BEGIN
  -- Get the period information
  SELECT
    *
  FROM
    c_period
  WHERE
    c_period_id = period_id INTO periodo;

  SELECT
    (periodo.startdate - interval '1 day')::date INTO initialDate;

  SELECT
    ARRAY (
      SELECT
        d
      FROM
        generate_series(periodo.startdate, periodo.enddate, INTERVAL '1 day') AS d
      WHERE
        EXTRACT(DOW FROM d) <> 0 -- 0 represents Sunday in PostgreSQL
) INTO dates_array;
  -- Output the array to verify the result
  -- RAISE NOTICE 'Dates array: %', dates_array;
  -- Create a temporary table to store the intermediate results
  CREATE TEMP TABLE temp_payments(
	  organizacion character varying,
	  ruta character varying,
    c_bpartner_id int,
    legacy_cartera_id numeric,
    fecha date,
    nombrecliente varchar,
    montobase numeric DEFAULT 0.00,
    interes numeric DEFAULT 0.00,
    saldoinicial numeric DEFAULT 0.00,
    date1 numeric DEFAULT 0.00,
    date2 numeric DEFAULT 0.00,
    date3 numeric DEFAULT 0.00,
    date4 numeric DEFAULT 0.00,
    date5 numeric DEFAULT 0.00,
    date6 numeric DEFAULT 0.00,
    date7 numeric DEFAULT 0.00,
    date8 numeric DEFAULT 0.00,
    date9 numeric DEFAULT 0.00,
    date10 numeric DEFAULT 0.00,
    date11 numeric DEFAULT 0.00,
    date12 numeric DEFAULT 0.00,
    date13 numeric DEFAULT 0.00,
    date14 numeric DEFAULT 0.00,
    date15 numeric DEFAULT 0.00,
    date16 numeric DEFAULT 0.00,
    date17 numeric DEFAULT 0.00,
    date18 numeric DEFAULT 0.00,
    date19 numeric DEFAULT 0.00,
    date20 numeric DEFAULT 0.00,
    date21 numeric DEFAULT 0.00,
    date22 numeric DEFAULT 0.00,
    date23 numeric DEFAULT 0.00,
    date24 numeric DEFAULT 0.00,
    date25 numeric DEFAULT 0.00,
    date26 numeric DEFAULT 0.00,
    date27 numeric DEFAULT 0.00,
    date28 numeric DEFAULT 0.00,
    date29 numeric DEFAULT 0.00,
    date30 numeric DEFAULT 0.00,
    date31 numeric DEFAULT 0.00,
    totalMovs numeric DEFAULT 0.00,
	  saldoFinal numeric DEFAULT 0.00
  );
  FOR bprec IN SELECT DISTINCT
  ru.name as ruta,
	  org.name as organizacion,
    bp.c_bpartner_id,
    bp.name AS nombrecliente
  FROM
    c_bpartner bp
    INNER JOIN legacy_cartera car ON bp.c_bpartner_id = car.c_bpartner_id 
	  inner join cv_ruta ru on bp.cv_ruta_id = ru.cv_ruta_id
	  inner join ad_org org on bp.ad_org_trx_id = org.ad_org_id
	  LOOP
    WITH prevdata AS (
      SELECT
        cd.*,
        get_abonos_up_to_date(cd.legacy_cartera_id::numeric, initialDate) AS abonos,
        cd.montototal - get_abonos_up_to_date(cd.legacy_cartera_id::numeric, initialDate) AS saldoparcial
      FROM
        legacy_cartera_data cd
      WHERE
        cd.c_bpartner_id = bprec.c_bpartner_id
        AND cd.fechaflat <= periodo.enddate
)
    SELECT
      pd.legacy_cartera_id,
      pd.fechaflat,
      sum(pd.monto) AS monto,
    sum(pd.interes) AS interes,
  sum(pd.abonos) AS abonos,
  sum(pd.saldoparcial) AS saldo
FROM
  prevdata pd
-- WHERE pd.saldoparcial > 0
GROUP BY
  pd.legacy_cartera_id,
  pd.fechaflat 
	  INTO carterarec;
    -- RAISE NOTICE 'cartera vigente para % %, Monto % Interés % Saldo %', bprec.c_bpartner_id, bprec.nombrecliente, carterarec.monto, carterarec.interes, carterarec.saldo;
    INSERT INTO temp_payments(organizacion, ruta, c_bpartner_id, legacy_cartera_id, fecha, nombrecliente, montobase, interes, saldoinicial)
      VALUES (bprec.organizacion, bprec.ruta, bprec.c_bpartner_id, carterarec.legacy_cartera_id, carterarec.fechaflat, bprec.nombrecliente, coalesce(carterarec.monto, 0.00), coalesce(carterarec.interes, 0.00), coalesce(carterarec.saldo, 0.00));
    -- let's iterate over the dates array
    totalCobros := 0.00;
    totalCreditos := 0.00;

    FOR i IN 1..array_length(dates_array, 1)
    LOOP
      -- RAISE NOTICE 'Processing date %', dates_array[i];
      SELECT
        coalesce(sum(cob.abono), 0)
      FROM
        legacy_cobro cob
      WHERE
        cob.operacion::date = dates_array[i]
        AND cob.c_bpartner_id = bprec.c_bpartner_id INTO cobro_value;
      cobros_array[i] := coalesce(cobro_value,0.00);
      totalCobros := totalCobros + coalesce(cobro_value,0.00);

      SELECT
        coalesce(sum(lcd.montototal))
      FROM
        legacy_cartera_data lcd
      WHERE
        lcd.c_bpartner_id = bprec.c_bpartner_id
        AND lcd.fechaflat = dates_array[i] INTO credito_value;
      creditos_array[i] = coalesce(credito_value,0.00);
      totalCreditos := totalCreditos + coalesce(credito_value,0.00);
      -- RAISE NOTICE 'Cobros para %: % bpid % credito % :: %', dates_array[i], cobros_array[i], bprec.c_bpartner_id, creditos_array[i], carterarec.legacy_cartera_id;
    END LOOP;
    -- map the cobros array to the temp table
    FOR i IN 1..array_length(cobros_array, 1)
    LOOP
      neto := coalesce(cobros_array[i], 0.00) - coalesce(creditos_array[i], 0.00);
      EXECUTE 'UPDATE temp_payments SET date' || i || ' = ' || neto || ' WHERE c_bpartner_id = ' || bprec.c_bpartner_id || ' and legacy_cartera_id = ' || coalesce(carterarec.legacy_cartera_id,0);
    END LOOP;

	saldoFinalValor := coalesce(carterarec.saldo, 0.00) + abs(totalCobros) - abs(totalCreditos);
	
    UPDATE
      temp_payments tp
    SET
      totalMovs = abs(totalCobros) + abs(totalCreditos),
		saldoFinal = saldoFinalValor
    WHERE
      tp.c_bpartner_id = bprec.c_bpartner_id
      AND tp.legacy_cartera_id = carterarec.legacy_cartera_id;

  END LOOP;
  -- Return the results from the temporary table
  RETURN QUERY
  SELECT
    tpm.*
  FROM
    temp_payments tpm
	  WHERE tpm.saldoinicial > 0 OR tpm.totalmovs > 0
  ORDER BY
	  tpm.fecha asc,
    tpm.nombrecliente asc;
  -- Drop the temporary table
  DROP TABLE temp_payments;
END;
$$;


--
-- Name: get_interes_up_to_date(numeric, date); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.get_interes_up_to_date(p_cartera_id numeric, p_upto_date date) RETURNS numeric
    LANGUAGE plpgsql
    AS $$
DECLARE
    credito_rec RECORD;
    dias_transcurridos INTEGER;
    dias_efectivos INTEGER;
    interes_calculado NUMERIC;
BEGIN
    -- 1. Buscar el registro del crédito en la tabla legacy_cartera.
    SELECT fecha, dias_cre, cuota
    INTO credito_rec
    FROM adempiere.legacy_cartera
    WHERE legacy_cartera_id = p_cartera_id;

    -- Si no se encuentra el crédito, no se puede calcular el interés.
    IF NOT FOUND THEN
        RETURN 0.00;
    END IF;

    -- 2. Calcular los días transcurridos desde el inicio del crédito hasta la fecha de corte.
    -- Se asegura que el cálculo no sea negativo si la fecha de corte es anterior a la de inicio.
    dias_transcurridos := GREATEST(0, p_upto_date::date - credito_rec.fecha::date);

    -- 3. Determinar los días efectivos para el cálculo.
    -- Se usa LEAST para tomar el valor menor entre los días transcurridos y el plazo total del crédito.
    -- Esto implementa la regla: el interés no se sigue acumulando después de la fecha de vencimiento.
    dias_efectivos := LEAST(dias_transcurridos, credito_rec.dias_cre);

    -- 4. Calcular el interés devengado multiplicando los días efectivos por la cuota diaria.
    interes_calculado := dias_efectivos * credito_rec.cuota;

    -- 5. Devolver el interés calculado, asegurándose de retornar 0.00 si el resultado es nulo.
    RETURN COALESCE(interes_calculado, 0.00);

END;
$$;


--
-- Name: FUNCTION get_interes_up_to_date(p_cartera_id numeric, p_upto_date date); Type: COMMENT; Schema: adempiere; Owner: -
--

COMMENT ON FUNCTION adempiere.get_interes_up_to_date(p_cartera_id numeric, p_upto_date date) IS 'Calcula el interés total devengado para un crédito desde su fecha de inicio hasta una fecha de corte específica. El cálculo se topa al plazo total del crédito (dias_cre).';


