--
-- Name: icc_credito_por_periodo(numeric); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.icc_credito_por_periodo(period_id numeric) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
  car RECORD;
  p RECORD;
  cobrec RECORD;
  bprec RECORD;
  personarec RECORD;
  credito_personarec RECORD;
  analista_rec RECORD;
  dias_por_vencer int;
  dias_vencidos int;
  creditovencido boolean DEFAULT FALSE;
  -- Variables para el cálculo de saldos
  interes_devengado numeric DEFAULT 0.00;
  total_abonos numeric DEFAULT 0.00;
  abono_a_interes numeric DEFAULT 0.00;
  abono_a_principal numeric DEFAULT 0.00;
  saldo_periodo_principal numeric DEFAULT 0.00;
  saldo_periodo_interes numeric DEFAULT 0.00;
  saldo_periodo_total numeric DEFAULT 0.00;
BEGIN
  -- CREATE TEMPORARY tables TO store the results OF the WITH queries
  DROP TABLE IF EXISTS riesgos;
  CREATE TEMP TABLE riesgos AS (
    SELECT
      value AS kname,
      description AS kdesc
    FROM
      AD_Ref_List
    WHERE
      AD_Reference_ID = 1000022
    );

  DROP TABLE IF EXISTS tipozona;
  CREATE TEMP TABLE tipozona AS (
    SELECT
      value AS kname,
      description AS kdesc
    FROM
      AD_Ref_List
    WHERE
      AD_Reference_ID = 1000021
    );

  DROP TABLE IF EXISTS estadosciviles;
  CREATE TEMP TABLE estadosciviles AS (
    SELECT
      value AS kname,
      description AS kdesc
    FROM
      AD_Ref_List
    WHERE
      AD_Reference_ID = 1000013
    );

  DROP TABLE IF EXISTS generos;
  CREATE TEMP TABLE generos AS (
    SELECT
      value AS kname,
      description AS kdesc
    FROM
      AD_Ref_List
    WHERE
      AD_Reference_ID = 1000020
    );

  DROP TABLE IF EXISTS analistas;
  CREATE TEMP TABLE analistas AS (
    SELECT
      ru.name AS nombreruta,
      ru.cv_ruta_id,
      coalesce(bp.taxid, '-'
      ) AS cedula,
      bp.name AS nombre_analista,
(
      SELECT
        kdesc
      FROM
        generos
      WHERE
        kname = bp.genero ) ::int AS id_genero
      FROM
        cv_ruta ru
      LEFT JOIN ad_user u ON ru.ad_user_id = u.ad_user_id
      LEFT JOIN c_bpartner bp ON u.c_bpartner_id = bp.c_bpartner_id
  );

  SELECT
    *
  FROM
    C_Period
  WHERE
    c_period_ID = period_id INTO p;
  DELETE FROM icc_credito
  WHERE c_period_id = period_id;


  /* inicia ciclo for */
  FOR car IN
  SELECT
    *
  FROM
    legacy_cartera_data cardata
  WHERE
  /* cardata.fecha::date BETWEEN p.startdate::date AND p.enddate::date
   OR fecha_ultimo_pago BETWEEN p.startdate::date AND p.enddate::date
   OR (fecha_ultimo_pago IS NULL AND cardata.fecha::date <= p.enddate::date) */
  cardata.fecha::date <= p.enddate::date
  AND ((cardata.montototal - get_abonos_up_to_date(cardata.legacy_cartera_id, p.enddate::date)) > 0
    OR cardata.fecha::date BETWEEN p.startdate::date AND p.enddate::date
    OR fecha_ultimo_pago BETWEEN p.startdate::date AND p.enddate::date)
  -- LIMIT 100 -- make it faster
  LOOP
    creditovencido := FALSE;
    --- verificar si el crédito está vencido
    IF (car.fecha_vencimiento::date < p.enddate::date) THEN
      -- dsds
      creditovencido := TRUE;
    END IF;

    SELECT
      coalesce(count(cob.*), 0.00) AS cantidad_cuotas,
      coalesce(sum(cob.abono), 0.00) AS total_abonos
    FROM
      legacy_cobro cob
    WHERE
      cob.id_cartera = car.legacy_cartera_id
      AND cob.operacion::date <= p.enddate::date
      AND cob.abono > 0 INTO cobrec;
    -- 1. Obtener el total de abonos realizados hasta la fecha de corte.
    total_abonos := adempiere.get_abonos_up_to_date(car.legacy_cartera_id, p.enddate::date);
    -- 2. Calcular el interés total que se ha devengado hasta la fecha de corte.
    interes_devengado := adempiere.get_interes_up_to_date(car.legacy_cartera_id, p.enddate::date);
    -- 3. Prorratear el total de abonos usando la tasa del crédito para saber qué porción va a interés y cuál a principal.
    abono_a_interes := total_abonos * car.tasa;
    abono_a_principal := total_abonos *(1 - car.tasa);
    -- 4. Calcular los saldos finales para el período.
    --    Al interés DEVENGADO se le resta la porción del abono que corresponde a interés.
    --    Al principal ORIGINAL se le resta la porción del abono que corresponde a principal.
    --    Se usa GREATEST para asegurar que los saldos nunca sean negativos.
    saldo_periodo_interes := GREATEST(0, interes_devengado - abono_a_interes);
    saldo_periodo_principal := GREATEST(0, car.monto - abono_a_principal);
    -- El saldo total es la suma de los saldos pendientes.
    saldo_periodo_total := saldo_periodo_principal + saldo_periodo_interes;

    dias_por_vencer :=(car.fecha_vencimiento::date - p.enddate::date);
    dias_vencidos :=(p.enddate::date - car.fecha_vencimiento::date);

    SELECT
      bp.*,
      coalesce(bpg.description, '7026101')::int AS id_destino_credito
    FROM
      c_bpartner bp
      INNER JOIN c_bp_group bpg ON bp.c_bp_group_id = bpg.c_bp_group_id
    WHERE
      c_bpartner_id = car.c_bpartner_id INTO bprec;
    ------------------------------------
