  SELECT
    icc_persona_id INTO v_persona_id
  FROM
    icc_persona
  WHERE
    c_period_id = p_data.c_period_id
    AND c_bpartner_id = p_data.c_bpartner_id;

  v_exists := FOUND;
  -- Check if any rows were returned
  IF v_exists THEN
    -- Update existing record
    UPDATE
      icc_persona
    SET
      (c_period_id,
        c_bpartner_id,
        fecha_nacimiento,
        id_actividad_economica,
        id_cedula_residencia,
        id_estado_civil,
