--

CREATE FUNCTION adempiere.productattribute(p_m_attributesetinstance_id numeric) RETURNS character varying
    LANGUAGE plpgsql STABLE
    AS $$

/*************************************************************************
 * The contents of this file are subject to the Compiere License.  You may
 * obtain a copy of the License at    http://www.compiere.org/license.html 
 * Software is on an  "AS IS" basis,  WITHOUT WARRANTY OF ANY KIND, either 
 * express or implied. See the License for details. Code: Compiere ERP+CRM
 * Copyright (C) 1999-2001 Jorg Janke, ComPiere, Inc. All Rights Reserved.
 *
 * converted to postgreSQL by Karsten Thiemann (Schaeffer AG), 
 * kthiemann@adempiere.org
 *************************************************************************
 * Title: Return Instance Attribute Info
 * Description:
 *  
 * Test:
    SELECT ProductAttribute (M_AttributeSetInstance_ID) 
    FROM M_InOutLine WHERE M_AttributeSetInstance_ID > 0
    --
    SELECT p.Name
    FROM C_InvoiceLine il LEFT OUTER JOIN M_Product p ON (il.M_Product_ID=p.M_Product_ID);
    SELECT p.Name || ProductAttribute (il.M_AttributeSetInstance_ID) 
    FROM C_InvoiceLine il LEFT OUTER JOIN M_Product p ON (il.M_Product_ID=p.M_Product_ID);
    
 ************************************************************************/

	
DECLARE

    v_Name          VARCHAR(2000) := '';
    v_NameAdd       VARCHAR(2000) := '';
    --
    v_Lot           M_AttributeSetInstance.Lot%TYPE;
    v_LotStart      M_AttributeSet.LotCharSOverwrite%TYPE;
    v_LotEnd        M_AttributeSet.LotCharEOverwrite%TYPE;
    v_SerNo         M_AttributeSetInstance.SerNo%TYPE;
    v_SerNoStart    M_AttributeSet.SerNoCharSOverwrite%TYPE;
    v_SerNoEnd      M_AttributeSet.SerNoCharEOverwrite%TYPE;
    v_GuaranteeDate M_AttributeSetInstance.GuaranteeDate%TYPE;
    
    r   RECORD;
    --

BEGIN
    --  Get Product Attribute Set Instance
    IF (p_M_AttributeSetInstance_ID > 0) THEN
        SELECT asi.Lot, asi.SerNo, asi.GuaranteeDate,
            COALESCE(a.SerNoCharSOverwrite, '#'::CHAR(1)), COALESCE(a.SerNoCharEOverwrite, ''::CHAR(1)),
            COALESCE(a.LotCharSOverwrite, chr(171)), COALESCE(a.LotCharEOverwrite, chr(187))
          INTO v_Lot, v_SerNo, v_GuaranteeDate,
            v_SerNoStart, v_SerNoEnd, v_LotStart, v_LotEnd
        FROM M_AttributeSetInstance asi
          INNER JOIN M_AttributeSet a ON (asi.M_AttributeSet_ID=a.M_AttributeSet_ID)
        WHERE asi.M_AttributeSetInstance_ID=p_M_AttributeSetInstance_ID;
        --
        IF (v_SerNo IS NOT NULL) THEN
            v_NameAdd := v_NameAdd || v_SerNoStart || v_SerNo || v_SerNoEnd || ' ';
        END IF;
        IF (v_Lot IS NOT NULL) THEN
            v_NameAdd := v_NameAdd || v_LotStart || v_Lot || v_LotEnd || ' ';
        END IF;
        IF (v_GuaranteeDate IS NOT NULL) THEN
            v_NameAdd := v_NameAdd || v_GuaranteeDate || ' ';
        END IF;
        --
        
        FOR r IN
	     SELECT ai.Value, a.Name
	        FROM M_AttributeInstance ai
	        INNER JOIN M_Attribute a ON (ai.M_Attribute_ID=a.M_Attribute_ID AND a.IsInstanceAttribute='Y')
        	WHERE ai.M_AttributeSetInstance_ID=p_M_AttributeSetInstance_ID
    	LOOP
            v_NameAdd := v_NameAdd || r.Name || ':' || r.Value || ' ';
        END LOOP;
        --
        IF (LENGTH(v_NameAdd) > 0) THEN
            v_Name := v_Name || ' (' || TRIM(v_NameAdd) || ')';
	ELSE 
	    v_Name := NULL;
        END IF;
    END IF;
    RETURN v_Name;
END;

$$;


--
-- Name: register_migration_script(character varying); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.register_migration_script(p_script character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_return CHARACTER VARYING;
    v_scriptid INTEGER;
BEGIN
    v_return := p_script || ' successfully registered';
    UPDATE AD_System
      SET LastMigrationScriptApplied=p_script
    WHERE LastMigrationScriptApplied<p_script
       OR LastMigrationScriptApplied IS NULL;
    SELECT MAX(AD_MigrationScript_ID)
	INTO v_scriptid
        FROM AD_MigrationScript
	WHERE Name = p_script;
    IF (v_scriptid IS NULL)
    THEN
        INSERT INTO ad_migrationscript 
            (isapply, scriptroll, ad_migrationscript_uu, 
             status, projectname, releaseno, 
             name, filename, ad_client_id, 
             ad_org_id, created, createdby, 
             updated, updatedby, isactive, 
             ad_migrationscript_id) 
        VALUES
            ('Y', 'N', generate_uuid(),
             'CO', 'iDempiere', (select releaseno from ad_system), 
             p_script, 'postgresql/'||p_script, 0, 
             0, now(), 100, 
             now(), 100, 'Y', 
             nextidfunc(53081,'N')); 
    ELSE
        v_return := p_script || ' was already applied';
        RAISE NOTICE '%', v_return;
	UPDATE ad_migrationscript
	    SET updated=now(), description = COALESCE(description, ' ') || ' reapplied'
	    WHERE ad_migrationscript_id = v_scriptid;
    END IF;
    RETURN v_return;
END;
$$;


--
-- Name: rep_balanza_dimensional(numeric, character); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.rep_balanza_dimensional(p_c_period_id numeric, p_aplica_calculo character) RETURNS TABLE(c_elementvalue_id_ numeric, parent_id_ numeric, value_ character varying, name_ character varying, accounttype_ character, accounttype_id_ integer, issummary_ character, nivelcuenta_ numeric, c_bpartner_value_ character varying, bpartner_name_ character varying, c_activity_value_ character varying, activity_name_ character varying, dimension_ integer, saldoid_ numeric, saldoih_ numeric, importe_saldo_inicial_ numeric, movd_ numeric, movh_ numeric, saldofd_ numeric, saldofh_ numeric, importe_saldo_final_ numeric, fecha_inicial_ date, fecha_final_ date)
    LANGUAGE plpgsql
    AS $$
DECLARE
	-------------------------------------------------------
	v_c_period_id NUMERIC(10, 0):= p_c_period_id;
	-------------------------------------------------------
BEGIN
	IF  (p_aplica_calculo='Y')
	THEN
		PERFORM adempiere.fnt_calcularsaldoshistoricos (1000000, 0);
	END IF;
	
	/*******************************************
	* A : ACTIVOS
	* O : CAPITAL DE ACCIONISTAS
	* E : GASTOS
	* R : INGRESOS
	* M : MEMO
	* L : PASIVO	 * 
	*******************************************/
	
	
	RETURN query(
		WITH RECURSIVE cte_balanza
		     AS
		     (
		     	SELECT
		     		cs.c_elementvalue_id,
		     		--cs.c_bpartner_id,
		     		cs.value,
		     		cs.name,
		     		cs.parent_id,
		     		cs.accounttype,
		     		cs.accounttype_id,
		     		cs.issummary,
		     		cs.nivelcuenta,
		     		cs.saldoid,
		     		cs.saldoih,
		     		cs.movd,
		     		cs.movh,
		     		cs.saldofd,
		     		cs.saldofh
		     	FROM
		     		cte_saldos AS cs
		     	WHERE
		     		NOT EXISTS
		     		(
		     			SELECT
		     				NULL
		     			FROM
		     				cte_saldos AS css
		     			WHERE
		     				css.parent_id = cs.c_elementvalue_id
		     		)
		     	UNION ALL
		     	SELECT
		     		cs.c_elementvalue_id,
		     		---cs.c_bpartner_id,
		     		cs.value,
		     		cs.name,
		     		cs.parent_id,
		     		cs.accounttype,
		     		cs.accounttype_id,
		     		cs.issummary,
		     		cs.nivelcuenta,
		     		COALESCE(cs.saldoid, cbl.saldoid) AS saldoid,
		     		COALESCE(cs.saldoih, cbl.saldoih) AS saldoih,
		     		COALESCE(cs.movd, cbl.movd) AS movd,
		     		COALESCE(cs.movh, cbl.movh) AS movh,
		     		COALESCE(cs.saldofd, cbl.saldofd) AS saldofd,
		     		COALESCE(cs.saldofh, cbl.saldofh) AS saldofh
		     	FROM
		     		cte_saldos        AS cs
		     		JOIN cte_balanza  AS cbl
		     			ON   cbl.parent_id= cs.c_elementvalue_id
		     )
		     
		     ,
		     
		     cte_ledger
		     AS
		     
		     (
		     	SELECT
		     		account_id,
		     		---c_bpartner_id,
		     		c_period_id,
		     		saldoid,
		     		saldoih,
		     		movd,
		     		movh,
		     		saldofd,
		     		saldofh
		     	FROM
		     		adempiere.nic_ledger
		     	WHERE
		     		c_period_id = v_c_period_id
		     ),
		     
		     
		     
		     
		     cte_ledger_dim AS
		     
		     (
		     	SELECT
		     		fac.account_id,
		     		ce."value",
		     		ce."name",
		     		cb."value",
		     		cb."name",
		     		fac.saldoid,
		     		fac.saldoih,
		     		fac.movd,
		     		fac.movh
		     	FROM
		     		adempiere.nic_ledger_dim AS fac
		     		JOIN adempiere.c_elementvalue   AS ce
		     			ON   fac.account_id=ce.c_elementvalue_id
		     		LEFT JOIN adempiere.c_bpartner  AS cb
		     			ON   fac.c_bpartner_id = cb.c_bpartner_id
		     	WHERE
		     		fac.c_period_id = v_c_period_id
		     ),
		     
		     cte_fechas AS
		     
		     (
		     	SELECT
		     		startdate::DATE     AS fecha_inicial,
		     		
		     		enddate::DATE       AS fecha_final
		     		
		     	FROM
		     		adempiere.c_period     p
		     	WHERE
		     		c_period_id = v_c_period_id
		     )
		     ,
		     
		     cte_saldos
		     AS
		     
		     (
		     	SELECT
		     		c.c_elementvalue_id,
		     		c.value,
		     		c.name,
		     		c.accounttype,
		     		c.nivelcuenta,
		     		CASE c.accounttype
		     			WHEN 'A' THEN 1
		     			WHEN 'L' THEN 2
		     			WHEN 'O' THEN 3
		     			WHEN 'R' THEN 4
		     			WHEN 'E' THEN 5
		     			ELSE 6
		     		END        AS accounttype_id,
		     		c.parent_id,
		     		c.issummary,
		     		---s.c_bpartner_id,
		     		s.saldoid  AS saldoid,
		     		s.saldoih  AS saldoih,
		     		s.movd     AS movd,
		     		s.movh     AS movh,
		     		s.saldofd  AS saldofd,
		     		s.saldofh  AS saldofh
		     	FROM
		     		adempiere.usr_v_cuentas_catalogo AS c
		     		LEFT OUTER JOIN LATERAL(
		     		     	SELECT
		     		     		nl.account_id,
		     		     		--- nl.c_bpartner_id,
		     		     		nl.c_period_id,
		     		     		nl.saldoid,
		     		     		nl.saldoih,
		     		     		nl.movd,
		     		     		nl.movh,
		     		     		nl.saldofd,
		     		     		nl.saldofh
		     		     	FROM
		     		     		cte_ledger AS nl
		     		     	WHERE
		     		     		nl.c_period_id = v_c_period_id
		     		     		AND nl.account_id = c.c_elementvalue_id
		     		     )     AS s
		     			ON   TRUE
		     )
		     
		     
		     SELECT
		     
		     bal.c_elementvalue_id, 
		     
		     bal.parent_id,
		     bal.value,
		     bal.name,
		     bal.accounttype,
		     bal.accounttype_id,
		     bal.issummary,
		     bal.nivelcuenta,
		     
		     NULL::CHARACTER VARYING AS c_bpartner_value,
		     NULL::CHARACTER VARYING AS bpartner_name,
		     
		     
		     NULL::CHARACTER VARYING AS c_activity_value,
		     NULL::CHARACTER VARYING AS activity_name,
		     
		     
		     1 AS dimension,
		     
		     
		     SUM(COALESCE(bal.saldoid, 0.00)) AS saldoid,
		     SUM(COALESCE(bal.saldoih, 0.00)) AS saldoih,
		     
		     
		     
		     /*******************************************
		     * A : ACTIVOS
		     * O : CAPITAL DE ACCIONISTAS
		     * E : GASTOS
		     * R : INGRESOS
		     * M : MEMO
		     * L : PASIVO	 * 
		     *******************************************/
		     
		     COALESCE(
		     	CASE bal.accounttype
		     		-- WHEN 'A' THEN (-1.00)*SUM(bal.saldoih -bal.saldoid)
		     		WHEN 'A' THEN  SUM(bal.saldoid - bal.saldoih ) 
		     		WHEN 'O' THEN (-1.00)*SUM(bal.saldoid -bal.saldoih)
		     		-- WHEN 'E' THEN (-1.00)*SUM(bal.saldoid -bal.saldoih)
		     		WHEN 'E' THEN SUM(bal.saldoid -bal.saldoih)
		     		
		     		WHEN 'R' THEN SUM(bal.saldoih -bal.saldoid)
		     		WHEN 'M' THEN SUM(bal.saldoid -bal.saldoih)
		     		WHEN 'L' THEN (-1.00)*SUM(bal.saldoid -bal.saldoih)
		     	END ,
		     	0.00
		     ) AS importe_saldo_inicial,
		     
		     
		     
		     
		     
		     SUM(COALESCE(bal.movd, 0.00)) AS movd,
		     SUM(COALESCE(bal.movh, 0.00)) AS movh,
		     
		     SUM(COALESCE(bal.saldofd, 0.00)) AS saldofd,
		     SUM(COALESCE(bal.saldofh, 0.00)) AS saldofh,
		     
		     
		     
		     COALESCE(
		     	CASE bal.accounttype
		     		--WHEN 'A' THEN (-1.00)*SUM(bal.saldofh -bal.saldofd)
		     		WHEN 'A' THEN SUM(bal.saldofd - bal.saldofh )
		     		WHEN 'O' THEN (-1.00)*SUM(bal.saldofd -bal.saldofh)
		     		--WHEN 'E' THEN (-1.00)*SUM(bal.saldofd -bal.saldofh)
		     		WHEN 'E' THEN SUM(bal.saldofd -bal.saldofh)
		     		WHEN 'R' THEN SUM(bal.saldofh -bal.saldofd)
		     		WHEN 'M' THEN SUM(bal.saldofd -bal.saldofh)
		     		WHEN 'L' THEN (-1.00)*SUM(bal.saldofd -bal.saldofh)
		     	END ,
		     	0.00
		     ) AS importe_saldo_final,
		     
		     
		     (
		     	SELECT
		     		fecha_inicial
		     	FROM
		     		cte_fechas
		     ) AS fecha_inicial,

		     
		     (
		     	SELECT
		     		fecha_final
		     	FROM
		     		cte_fechas
		     ) AS fecha_final
		     
		     
		     FROM
		     cte_balanza AS bal
		     
		     
		     
		     GROUP BY
		     bal.c_elementvalue_id,
		     
		     bal.parent_id,
		     bal.value,
		     bal.name,
		     bal.accounttype,
		     bal.accounttype_id,
		     bal.issummary,
		     bal.nivelcuenta
		     HAVING
		     SUM(COALESCE(bal.saldoid, 0.00)) !=0
		OR SUM(COALESCE(bal.saldoih, 0.00)) !=0
		OR SUM(COALESCE(bal.movd, 0.00)) !=0
		OR SUM(COALESCE(bal.movh, 0.00))!=0
		OR SUM(COALESCE(bal.saldofd, 0.00)) !=0
		OR SUM(COALESCE(bal.saldofh, 0.00)) !=0
		   
		   
		   UNION ALL
		   
		   
		   
		   SELECT
		   	c.c_elementvalue_id,
		   	c.parent_id,
		   	c.value,
		   	c.name,
		   	c.accounttype,
		   	CASE c.accounttype
		   		WHEN 'A' THEN 1
		   		WHEN 'L' THEN 2
		   		WHEN 'O' THEN 3
		   		WHEN 'R' THEN 4
		   		WHEN 'E' THEN 5
		   		ELSE 6
		   	END                          AS accounttype_id,
		   	c.issummary,
		   	c.nivelcuenta,
		   	cb.value::CHARACTER VARYING  AS c_bpartner_value,

		   	-- COALESCE(initcap(cb.name), 'N/D')::CHARACTER VARYING AS bpartner_name,
		   	COALESCE(initcap(cb.name),  case when c.accounttype<> 'R'  then  'N/D' else c.name end  )::CHARACTER VARYING AS bpartner_name,
		   	
		   	
		   	NULL::CHARACTER VARYING      AS c_activity_value,
		   	NULL::CHARACTER VARYING      AS activity_name,
		   	2                            AS dimension,
		   	SUM(fac.saldoid)             AS saldoid,
		   	SUM(fac.saldoih)             AS saldoih,
		   	COALESCE(
		   		CASE c.accounttype
		   			--WHEN 'A' THEN (-1.00)*SUM(fac.saldoih -fac.saldoid)
		   			WHEN 'A' THEN SUM(fac.saldoid - fac.saldoih )
		   			WHEN 'O' THEN (-1.00)*SUM(fac.saldoid -fac.saldoih)
		   			--WHEN 'E' THEN (-1.00)*SUM(fac.saldoid -fac.saldoih)
					WHEN 'E' THEN SUM(fac.saldoid -fac.saldoih)		   			
		   			WHEN 'R' THEN SUM(fac.saldoih -fac.saldoid)
		   			WHEN 'M' THEN SUM(fac.saldoid -fac.saldoih)
		   			WHEN 'L' THEN (-1.00)*SUM(fac.saldoid -fac.saldoih)
		   		END ,
		   		0.00
		   	)                            AS importe_saldo_inicial,
		   	SUM(fac.movd)                AS saldoih,
		   	SUM(fac.movh)                AS movh,
		   	SUM(fac.saldofd)             AS saldofd,
		   	SUM(fac.saldofh)             AS saldofh,
		   	COALESCE(
		   		CASE c.accounttype
		   			--WHEN 'A' THEN (-1.00)*SUM(fac.saldofh -fac.saldofd)
		   			WHEN 'A' THEN SUM(fac.saldofd - fac.saldofh )
		   			WHEN 'O' THEN (-1.00)*SUM(fac.saldofd -fac.saldofh)
		   			--WHEN 'E' THEN (-1.00)*SUM(fac.saldofd -fac.saldofh)
		   			WHEN 'E' THEN SUM(fac.saldofd -fac.saldofh)
		   			WHEN 'R' THEN SUM(fac.saldofh -fac.saldofd)
		   			WHEN 'M' THEN SUM(fac.saldofd -fac.saldofh)
		   			WHEN 'L' THEN (-1.00)*SUM(fac.saldofd -fac.saldofh)
		   		END ,
		   		0.00
		   	)                            AS importe_saldo_final,
		   	(
		   		SELECT
		   			fecha_inicial
		   		FROM
		   			cte_fechas
		   	)                            AS fecha_inicial,
		   	(
		   		SELECT
		   			fecha_final
		   		FROM
		   			cte_fechas
		   	)                            AS fecha_final
		   FROM
		   	adempiere.nic_ledger_dim     AS fac                           
		   	JOIN adempiere.usr_v_cuentas_catalogo  AS c                                  
		   		ON   fac.account_id=c.c_elementvalue_id
		   	LEFT JOIN adempiere.c_bpartner         AS cb
		   		ON   fac.c_bpartner_id = cb.c_bpartner_id
		   WHERE
		   	fac.c_period_id = v_c_period_id
		   GROUP BY
		   	c_elementvalue_id,
		   	parent_id,
		   	c.value,
		   	c.name,
		   	accounttype,
		   	accounttype_id,
		   	c.issummary,
		   	nivelcuenta,
		   	c_bpartner_value,
		   	bpartner_name,
		   	dimension
		   
		   
		   UNION ALL
		   
		   
		   
		   SELECT
		   	c.c_elementvalue_id,
		   	c.parent_id,
		   	c.value,
		   	c.name,
		   	c.accounttype,
		   	CASE c.accounttype
		   		WHEN 'A' THEN 1
		   		WHEN 'L' THEN 2
		   		WHEN 'O' THEN 3
		   		WHEN 'R' THEN 4
		   		WHEN 'E' THEN 5
		   		ELSE 6
		   	END                          AS accounttype_id,
		   	c.issummary,
		   	c.nivelcuenta,
		   	NULL::CHARACTER VARYING      AS c_bpartner_value,
		   	NULL::CHARACTER VARYING      AS bpartner_name,
		   	a.value::CHARACTER VARYING      c_activity_value,

		   	--COALESCE(initcap(a.name), 'N/D')::CHARACTER VARYING AS   	activity_name,
		   	COALESCE(initcap(a.name), case when c.accounttype<> 'R'  then  'N/D' else c.name  end )::CHARACTER VARYING AS   	activity_name,

		   	
		   	3                            AS dimension,
		   	SUM(fac.saldoid)             AS saldoid,
		   	SUM(fac.saldoih)             AS saldoih,
		   	COALESCE(
		   		CASE c.accounttype
		   			--WHEN 'A' THEN (-1.00)*SUM(fac.saldoih -fac.saldoid)
		   			WHEN 'A' THEN SUM(fac.saldoid - fac.saldoih )
		   			WHEN 'O' THEN (-1.00)*SUM(fac.saldoid -fac.saldoih)
		   			--WHEN 'E' THEN (-1.00)*SUM(fac.saldoid -fac.saldoih)
		   			WHEN 'E' THEN SUM(fac.saldoid -fac.saldoih)
		   			WHEN 'R' THEN SUM(fac.saldoih -fac.saldoid)
		   			WHEN 'M' THEN SUM(fac.saldoid -fac.saldoih)
		   			WHEN 'L' THEN (-1.00)*SUM(fac.saldoid -fac.saldoih)
		   		END ,
		   		0.00
		   	)                            AS importe_saldo_inicial,
		   	SUM(fac.movd)                AS saldoih,
		   	SUM(fac.movh)                AS movh,
		   	SUM(fac.saldofd)             AS saldofd,
		   	SUM(fac.saldofh)             AS saldofh,
		   	COALESCE(
		   		CASE c.accounttype
		   			--WHEN 'A' THEN (-1.00)*SUM(fac.saldofh -fac.saldofd)
		   			WHEN 'A' THEN SUM(fac.saldofd - fac.saldofh )
		   			WHEN 'O' THEN (-1.00)*SUM(fac.saldofd -fac.saldofh)
		   			--WHEN 'E' THEN (-1.00)*SUM(fac.saldofd -fac.saldofh)
		   			WHEN 'E' THEN SUM(fac.saldofd -fac.saldofh)
		   			WHEN 'R' THEN SUM(fac.saldofh -fac.saldofd)
		   			WHEN 'M' THEN SUM(fac.saldofd -fac.saldofh)
		   			WHEN 'L' THEN (-1.00)*SUM(fac.saldofd -fac.saldofh)
		   		END ,
		   		0.00
		   	)                            AS importe_saldo_final,
		   	(
		   		SELECT
		   			fecha_inicial
		   		FROM
		   			cte_fechas
		   	)                            AS fecha_inicial,
		   	(
		   		SELECT
		   			fecha_final
		   		FROM
		   			cte_fechas
		   	)                            AS fecha_final
		   FROM
		   	adempiere.nic_ledger_dim     AS fac
		   	JOIN adempiere.usr_v_cuentas_catalogo  AS c
		   		ON   fac.account_id=c.c_elementvalue_id
		   	LEFT JOIN adempiere.c_activity AS a
		   		ON   fac.c_activity_id=a.c_activity_id
		   WHERE
		   	fac.c_period_id = v_c_period_id
		   GROUP BY
		   	c_elementvalue_id,
		   	parent_id,
		   	c.value,
		   	c.name,
		   	accounttype,
		   	accounttype_id,
		   	c.issummary,
		   	nivelcuenta,
		   	c_activity_value,
		   	activity_name,
		   	dimension
		   ORDER BY
		   	accounttype_id ASC,
		   	VALUE ASC,
		   	dimension ASC,
		   	bpartner_name ASC,
		   	activity_name ASC
	);
END;
$$;


--
-- Name: rep_comprobante_diario(numeric, numeric, numeric); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.rep_comprobante_diario(p_ad_client_id numeric, p_gl_journal_id numeric, p_period_id numeric) RETURNS TABLE(periodo_ character varying, dateacct_ date, c_elementvalue_id_ numeric, codigocuenta_ character varying, nombrecuenta_ character varying, description_ character varying, parcial_ numeric, debitos_ numeric, creditos_ numeric, ctamayor_id_ numeric, ctmayor_name_ character varying, ctmayor_value_ character varying, centrocosto_ character varying, nota_contable_ character varying, descripcion_nota_ character varying, docstatus_nota_ character varying, status_contable_ character varying, origen_ character varying)
    LANGUAGE plpgsql
    AS $$
	
	DECLARE 
	
	v_cliente_id NUMERIC:=p_ad_client_id;
	v_nota_id NUMERIC:=p_gl_journal_id; 
	v_periodo_id NUMERIC:= p_period_id;
BEGIN
	RETURN query(
		WITH cte_journals_id
		     
		     AS 
		     
		     (
		     	SELECT
		     		gl.gl_journal_id,
		     		adempiere.fnt_estado_contable_nota (gl.gl_journal_id) AS 
		     		status_contable
		     	FROM
		     		adempiere.gl_journal AS gl
		     	WHERE
		     		gl_journal_id = v_nota_id
		     	GROUP BY
		     		gl.gl_journal_id
		     ), 
		     cte_journals_account
		     AS
		     
		     (
		     	SELECT
		     		jnl_line.gl_journal_id,
		     		jnl_line.description,
		     		jnl_line.dateacct,
		     		jnl_line.amtacctdr,
		     		jnl_line.amtacctcr,
		     		jnl_line.c_validcombination_id,
		     		vc.account_id,
		     		vcc.c_elementvalue_id AS c_elementvalue_id,
		     		vcc.value  AS codigocuenta,
		     		vcc.name   AS nombrecuenta,
		     		adempiere.fn_usr_getparentid_upto (vcc.c_elementvalue_id, 1) AS 
		     		ctamayor_id,
		     		COALESCE(
		     			(
		     				SELECT
		     					description
		     				FROM
		     					adempiere.c_elementvalue aux
		     				WHERE
		     					aux.c_elementvalue_id = vcc.ctamayor_id
		     			),
		     			''
		     		)          AS ctmayor_name,
		     		COALESCE(
		     			(
		     				SELECT
		     					VALUE
		     				FROM
		     					adempiere.usr_v_cuentas_catalogo AS aux
		     				WHERE
		     					aux.c_elementvalue_id = vcc.ctamayor_id
		     			),
		     			''
		     		)          AS ctmayor_value,
		     		(
		     			SELECT
		     				"value"||' - '||
		     				"name"
		     			FROM
		     				adempiere.ad_org AS org
		     			WHERE
		     				org.ad_org_id = vc.ad_org_id
		     		)          AS centrocosto
		     	FROM
		     		adempiere.gl_journalline AS jnl_line
		     		INNER JOIN adempiere.c_validcombination AS vc
		     			ON   vc.c_validcombination_id = jnl_line.c_validcombination_id
		     		INNER JOIN cte_journals_id AS jnl_ids
		     			ON   jnl_ids.gl_journal_id=jnl_line.gl_journal_id
		     		INNER JOIN adempiere.usr_v_cuentas_catalogo_ctamayor AS vcc
		     			ON   vcc.c_elementvalue_id=vc.account_id
		     )
		     SELECT
		     periodo.name AS periodo,
		     gl.dateacct::DATE AS dateacct,
		     gl.c_elementvalue_id,
		     gl.codigocuenta,
		     gl.nombrecuenta,
		     gl.description AS description,
		     (gl.amtacctdr + gl.amtacctcr) AS parcial,
		     gl.amtacctdr AS debitos,
		     gl.amtacctcr AS creditos, 
		     gl.ctamayor_id,
		     gl.ctmayor_name,
		     gl.ctmayor_value,
		     gl.centrocosto::CHARACTER VARYING,
		     
		     glparent.documentno AS nota_contable,
		     glparent.description AS descripcion_nota,		     		
		     
		     adempiere.fnt_docstatus (glparent.docstatus) AS 
		     
		     docstatus_nota, 
		     
		     jnl_ids.status_contable,
		     
		     COALESCE(
		     	(
		     		SELECT
		     			description
		     		FROM
		     			adempiere.gl_category AS paq
		     		WHERE
		     			paq.gl_category_id = glparent.gl_category_id  
		     	),
		     	'Ninguno'
		     ) ::CHARACTER VARYING AS origen 
		     
		     
		     FROM
		     cte_journals_account AS gl
		     INNER JOIN cte_journals_id AS jnl_ids
		     ON jnl_ids.gl_journal_id=gl.gl_journal_id
		     INNER JOIN adempiere.gl_journal AS glparent
		     ON gl.gl_journal_id=glparent.gl_journal_id
		     INNER JOIN adempiere.c_period AS periodo
		     ON glparent.c_period_id=periodo.c_period_id
		     WHERE
		     glparent.gl_journal_id = v_nota_id
		AND (gl.amtacctdr!=0 OR gl.amtacctcr!=0)
	);
END;
$$;


--
-- Name: role_access_update(); Type: FUNCTION; Schema: adempiere; Owner: -
--

CREATE FUNCTION adempiere.role_access_update() RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
   roleaccesslevel      VARCHAR (200);
   roleaccesslevelwin   VARCHAR (200);
   sqlins               VARCHAR (2000);
   r                    RECORD;
BEGIN
   FOR r IN (SELECT   ad_role_id, userlevel, NAME, ad_client_id, ad_org_id
                 FROM AD_ROLE
                WHERE ismanual = 'N' AND isactive = 'Y'
             ORDER BY ad_role_id)
   LOOP
      IF r.userlevel = 'S  '                                        -- system
      THEN
         roleaccesslevel := '(''4'',''7'',''6'')';
         roleaccesslevelwin := roleaccesslevel;
      ELSIF r.userlevel = ' C '                                      -- client
      THEN
         roleaccesslevel := '(''7'',''6'',''3'',''2'')';
         roleaccesslevelwin := roleaccesslevel;
      ELSIF r.userlevel = ' CO'                                -- client + org
      THEN
         roleaccesslevel := '(''7'',''6'',''3'',''2'',''1'')';
         roleaccesslevelwin := roleaccesslevel;
      ELSE                                                    -- org or others
         roleaccesslevel := '(''3'',''1'',''7'')';
         roleaccesslevelwin :=
                        roleaccesslevel || ' AND w.Name NOT LIKE ''%(all)%''';
      END IF;

      sqlins :=
            'INSERT INTO AD_Window_Access (AD_Window_ID, AD_Role_ID, AD_Client_ID,AD_Org_ID,IsActive,Created,CreatedBy,Updated,UpdatedBy,IsReadWrite) SELECT DISTINCT w.AD_Window_ID, '
         || r.ad_role_id
         || ','||r.ad_client_id||','||r.ad_org_id||',''Y'', statement_timestamp(),0, statement_timestamp(),0,''Y'' FROM AD_Window w INNER JOIN AD_Tab t ON (w.AD_Window_ID=t.AD_Window_ID) INNER JOIN AD_Table tt ON (t.AD_Table_ID=tt.AD_Table_ID) LEFT JOIN AD_Window_Access wa ON (wa.AD_Role_ID='
	 || r.ad_role_id
	 || ' AND w.AD_Window_ID = wa.AD_Window_ID) WHERE w.IsActive = ''Y'' AND wa.AD_Window_ID IS NULL AND t.SeqNo=(SELECT MIN(SeqNo) FROM AD_Tab xt WHERE xt.AD_Window_ID=w.AD_Window_ID) AND tt.AccessLevel IN '
         || roleaccesslevelwin;

      EXECUTE sqlins;

      sqlins :=
            'INSERT INTO AD_Process_Access (AD_Process_ID, AD_Role_ID, AD_Client_ID,AD_Org_ID,IsActive,Created,CreatedBy,Updated,UpdatedBy,IsReadWrite) SELECT DISTINCT p.AD_Process_ID, '
         || r.ad_role_id
         || ','||r.ad_client_id||','||r.ad_org_id||',''Y'', statement_timestamp(),0, statement_timestamp(),0,''Y'' FROM AD_Process p LEFT JOIN AD_Process_Access pa ON (pa.AD_Role_ID='
	 || r.ad_role_id
	 || ' AND p.AD_Process_ID = pa.AD_Process_ID) WHERE p.IsActive = ''Y'' AND pa.AD_Process_ID IS NULL AND AccessLevel IN '
         || roleaccesslevel;

      EXECUTE sqlins;

      sqlins :=
            'INSERT INTO AD_Form_Access (AD_Form_ID, AD_Role_ID, AD_Client_ID,AD_Org_ID,IsActive,Created,CreatedBy,Updated,UpdatedBy,IsReadWrite) SELECT f.AD_Form_ID, '
         || r.ad_role_id
         || ','||r.ad_client_id||','||r.ad_org_id||',''Y'', statement_timestamp(),0, statement_timestamp(),0,''Y'' FROM AD_Form f LEFT JOIN AD_Form_Access fa ON (fa.AD_Role_ID='
	 || r.ad_role_id
	 || ' AND f.AD_Form_ID = fa.AD_Form_ID) WHERE f.IsActive = ''Y'' AND fa.AD_Form_ID IS NULL AND AccessLevel IN '
         || roleaccesslevel;

      EXECUTE sqlins;

      sqlins :=
            'INSERT INTO AD_WorkFlow_Access (AD_WorkFlow_ID, AD_Role_ID, AD_Client_ID,AD_Org_ID,IsActive,Created,CreatedBy,Updated,UpdatedBy,IsReadWrite) SELECT w.AD_WorkFlow_ID, '
         || r.ad_role_id
         || ','||r.ad_client_id||','||r.ad_org_id||',''Y'', statement_timestamp(),0, statement_timestamp(),0,''Y'' FROM AD_WorkFlow w LEFT JOIN AD_WorkFlow_Access wa ON (wa.AD_Role_ID='
	 || r.ad_role_id
	 || ' AND w.AD_WorkFlow_ID = wa.AD_WorkFlow_ID) WHERE w.IsActive = ''Y'' AND w.AD_Client_ID IN (0,'||r.ad_client_id||') AND wa.AD_WorkFlow_ID IS NULL AND AccessLevel IN '
         || roleaccesslevel;

      EXECUTE sqlins;

      sqlins :=
            'INSERT INTO AD_Document_Action_Access (AD_Client_ID,AD_Org_ID,IsActive,Created,CreatedBy,Updated,UpdatedBy,C_DocType_ID, AD_Ref_List_ID, AD_Role_ID) (SELECT '
	 || r.ad_client_id || ',' || r.ad_org_id 
	 || ',''Y'', statement_timestamp(),0, statement_timestamp(),0, doctype.C_DocType_ID, action.AD_Ref_List_ID, rol.AD_Role_ID '
         || ' FROM AD_Client client INNER JOIN C_DocType doctype ON (doctype.AD_Client_ID=client.AD_Client_ID) INNER JOIN AD_Ref_List action ON (action.AD_Reference_ID=135) INNER JOIN AD_Role rol ON (rol.AD_Client_ID=client.AD_Client_ID AND rol.AD_Role_ID='
	 || r.ad_role_id
         || ') LEFT JOIN AD_Document_Action_Access da ON (da.AD_Role_ID='
