--

CREATE FUNCTION adempiere.sms_create(mk character varying) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE 
 cobro record;
 bp record;
 org record;
 nextseq numeric;
 messagestr character varying; 
 shouldSendSMS character varying;
 cleanphone character varying;
 validPhone boolean;
BEGIN  
	select * from legacy_cobro where masterkey = mk into cobro;
	select o_nextid::numeric from nextid((select ad_sequence_id 
	from ad_sequence where name = 'notification')::Integer, 'Y'::Varchar)
	into nextseq;
	select * from c_bpartner where c_bpartner_id = cobro.c_bpartner_id into bp;
	messagestr := 'Estimado (a) cliente [clientname], hemos recibido una cuota de su parte '
	|| 'por [montocuota] córdobas. GRUPO DUHO le agradece por su pago.';
	messagestr := replace(messagestr, '[clientname]', bp.name);
	messagestr := replace(messagestr, '[montocuota]', cobro.abono::character varying);
	cleanphone := coalesce(trim(replace(bp.quickphone, ' ', '')),'');
	validPhone := case when length(cleanphone)>=8 then true else  false end;

	select * from ad_org where ad_org_id = cobro.ad_org_id into org;
	shouldSendSMS := coalesce(org.memo::json->>'shouldSendSMS', 'N');
	raise notice 'Org shouldSendSMS: % - % - % - %', org.name, shouldSendSMS, cleanphone, validPhone;
	if('Y'::character varying=shouldSendSMS and validPhone and cobro.abono>0) then
		INSERT INTO adempiere.notification(
		notification_id, ad_client_id, ad_org_id, 
		isactive, created, createdby, updated, 
		updatedby, messagebody, destination, 
		currentstatus, generatedby, attempts, 
		result, sentfrom, notificationtype,
		notificationsource, lastattempt, notification_uu)
		VALUES (nextseq, cobro.ad_client_id, cobro.ad_org_id,
				'Y', now(), 0, now(),
				0, messagestr, cleanphone, 
				'SCHEDULED', cobro.createdby, 0,
				null, null, 'legacy_cobro',
				cobro::character varying, null, uuid_generate_v4());
		return;
		raise notice 'final: %', messagestr;
	end if;
	raise notice 'Nothing scheduled';
	return;
END;
$$;


