--
-- Name: aad_pmovs; Type: VIEW; Schema: adempiere; Owner: -
--

CREATE VIEW adempiere.aad_pmovs AS
 SELECT suc.ad_org_id,
    suc.value AS codsucursal,
    suc.name AS sucursal,
    p.c_payment_id,
    (p.dateacct)::date AS fecha,
    p.documentno,
    dt.docbasetype,
    p.payamt AS monto,
        CASE
            WHEN (p.c_charge_id = (1000032)::numeric) THEN 'Cobro'::text
            WHEN (p.c_charge_id = (1000030)::numeric) THEN 'Desembolso'::text
            WHEN ((p.c_charge_id = ANY (ARRAY[(1000031)::numeric, (1000034)::numeric])) AND (dt.docbasetype = 'ARR'::bpchar)) THEN 'Traslado+'::text
            WHEN ((p.c_charge_id = ANY (ARRAY[(1000031)::numeric, (1000034)::numeric])) AND (dt.docbasetype = 'APP'::bpchar)) THEN 'Traslado-'::text
            WHEN ((p.c_charge_id IS NOT NULL) AND (p.c_charge_id <> ALL (ARRAY[(1000032)::numeric, (1000030)::numeric, (1000031)::numeric, (1000034)::numeric]))) THEN 'Gasto'::text
            WHEN ((p.c_charge_id IS NULL) AND (dt.docbasetype = 'ARR'::bpchar)) THEN 'Cobro'::text
            WHEN ((p.c_charge_id IS NULL) AND (dt.docbasetype = 'APP'::bpchar)) THEN 'Gasto Unident'::text
            ELSE 'NaCharge'::text
        END AS tipomov,
    p.description AS concepto,
    bp.c_bpartner_id,
    bp.value AS codtercero,
    bp.name AS tercero,
    bac.c_bankaccount_id,
    bac.value AS codcaja,
    bac.name AS caja
   FROM ((((adempiere.c_payment p
     JOIN adempiere.ad_org suc ON ((p.ad_org_id = suc.ad_org_id)))
     JOIN adempiere.c_doctype dt ON ((p.c_doctype_id = dt.c_doctype_id)))
     JOIN adempiere.c_bpartner bp ON ((p.c_bpartner_id = bp.c_bpartner_id)))
     JOIN adempiere.c_bankaccount bac ON ((p.c_bankaccount_id = bac.c_bankaccount_id)));


