CREATE VIEW adempiere.ad_tab_v AS
 SELECT t.ad_tab_id,
    t.ad_window_id,
    t.ad_table_id,
    t.name,
    t.description,
    t.help,
    t.seqno,
    t.issinglerow,
    t.hastree,
    t.isinfotab,
    tbl.replicationtype,
    tbl.tablename,
    tbl.accesslevel,
    tbl.issecurityenabled,
    tbl.isdeleteable,
    COALESCE(t.ishighvolume, tbl.ishighvolume) AS ishighvolume,
    tbl.isview,
    'N'::character(1) AS hasassociation,
    t.istranslationtab,
    t.isreadonly,
    t.ad_image_id,
    t.tablevel,
    t.whereclause,
    t.orderbyclause,
    t.commitwarning,
    t.readonlylogic,
    t.displaylogic,
    t.ad_column_id,
    t.ad_process_id,
    t.issorttab,
    t.isinsertrecord,
    t.isadvancedtab,
    t.ad_columnsortorder_id,
    t.ad_columnsortyesno_id,
    t.included_tab_id,
    t.parent_column_id,
    t.ad_tab_uu,
    tbl.ad_table_uu,
    t.treedisplayedon,
    t.maxqueryrecords,
    t.isallowadvancedlookup,
    t.islookuponlyselection,
    t.ad_tabtype,
    t.entitytype
   FROM (adempiere.ad_tab t
     JOIN adempiere.ad_table tbl ON ((t.ad_table_id = tbl.ad_table_id)))
  WHERE ((t.isactive = 'Y'::bpchar) AND (tbl.isactive = 'Y'::bpchar));


