    i.processed,
    i.c_doctype_id,
    i.c_doctypetarget_id,
    i.c_order_id,
    i.description,
    i.isapproved,
    i.istransferred,
    i.salesrep_id,
    i.dateinvoiced,
    i.dateprinted,
    i.dateacct,
    i.c_bpartner_id,
    i.c_bpartner_location_id,
    i.ad_user_id,
    i.poreference,
    i.dateordered,
    i.c_currency_id,
    i.c_conversiontype_id,
    i.paymentrule,
    i.c_paymentterm_id,
    i.c_charge_id,
    i.m_pricelist_id,
    i.c_campaign_id,
    i.c_project_id,
    i.c_activity_id,
    i.isprinted,
    i.isdiscountprinted,
    i.ispaid,
    i.isindispute,
    i.ispayschedulevalid,
    NULL::numeric AS c_invoicepayschedule_id,
    i.invoicecollectiontype,
    i.dunninggrace,
        CASE
            WHEN ((adempiere.charat((d.docbasetype)::character varying, 3))::text = 'C'::text) THEN (i.chargeamt * '-1'::numeric)
            ELSE i.chargeamt
        END AS chargeamt,
        CASE
            WHEN ((adempiere.charat((d.docbasetype)::character varying, 3))::text = 'C'::text) THEN (i.totallines * '-1'::numeric)
            ELSE i.totallines
