--
-- Name: c_invoiceline_v; Type: VIEW; Schema: adempiere; Owner: -
--

CREATE VIEW adempiere.c_invoiceline_v AS
 SELECT il.ad_client_id,
    il.ad_org_id,
    il.c_invoiceline_id,
    i.c_invoice_id,
    i.salesrep_id,
    i.c_bpartner_id,
    il.m_product_id,
    i.documentno,
    i.dateinvoiced,
    i.dateacct,
    i.issotrx,
    i.docstatus,
    adempiere.currencyround((i.multiplier * il.linenetamt), i.c_currency_id, 'N'::character varying) AS linenetamt,
    adempiere.currencyround(((i.multiplier * il.pricelist) * il.qtyinvoiced), i.c_currency_id, 'N'::character varying) AS linelistamt,
        CASE
            WHEN (COALESCE(il.pricelimit, (0)::numeric) = (0)::numeric) THEN adempiere.currencyround((i.multiplier * il.linenetamt), i.c_currency_id, 'N'::character varying)
            ELSE adempiere.currencyround(((i.multiplier * il.pricelimit) * il.qtyinvoiced), i.c_currency_id, 'N'::character varying)
        END AS linelimitamt,
    adempiere.currencyround((((i.multiplier * il.pricelist) * il.qtyinvoiced) - il.linenetamt), i.c_currency_id, 'N'::character varying) AS linediscountamt,
        CASE
            WHEN (COALESCE(il.pricelimit, (0)::numeric) = (0)::numeric) THEN (0)::numeric
            ELSE adempiere.currencyround(((i.multiplier * il.linenetamt) - (il.pricelimit * il.qtyinvoiced)), i.c_currency_id, 'N'::character varying)
        END AS lineoverlimitamt,
    il.qtyinvoiced,
    il.qtyentered,
    il.line,
    il.c_orderline_id,
    il.c_uom_id,
    il.c_campaign_id,
    il.c_project_id,
    il.c_activity_id,
    il.c_projectphase_id,
    il.c_projecttask_id
   FROM adempiere.c_invoice_v i,
    adempiere.c_invoiceline il
  WHERE (i.c_invoice_id = il.c_invoice_id);


