CREATE VIEW adempiere.c_order_linetax_vt AS
 SELECT ol.ad_client_id,
    ol.ad_org_id,
    ol.isactive,
    ol.created,
    ol.createdby,
    ol.updated,
    ol.updatedby,
    uomt.ad_language,
