    a_name,
    a_street,
    a_city,
    a_state,
    a_zip,
    a_ident_dl,
    a_ident_ssn,
    a_email,
    voiceauthcode,
    orig_trxid,
    ponum,
    c_currency_id,
    c_conversiontype_id,
        CASE isreceipt
            WHEN 'Y'::bpchar THEN payamt
            ELSE (payamt * ('-1'::integer)::numeric)
        END AS payamt,
        CASE isreceipt
            WHEN 'Y'::bpchar THEN discountamt
            ELSE (discountamt * ('-1'::integer)::numeric)
        END AS discountamt,
        CASE isreceipt
            WHEN 'Y'::bpchar THEN writeoffamt
            ELSE (writeoffamt * ('-1'::integer)::numeric)
        END AS writeoffamt,
        CASE isreceipt
            WHEN 'Y'::bpchar THEN taxamt
            ELSE (taxamt * ('-1'::integer)::numeric)
        END AS taxamt,
        CASE isreceipt
            WHEN 'Y'::bpchar THEN overunderamt
            ELSE (overunderamt * ('-1'::integer)::numeric)
        END AS overunderamt,
        CASE isreceipt
            WHEN 'Y'::bpchar THEN 1
            ELSE '-1'::integer
        END AS multiplierap,
