--
-- Name: caf_accounts_balance; Type: VIEW; Schema: adempiere; Owner: -
--

CREATE VIEW adempiere.caf_accounts_balance AS
 WITH RECURSIVE ancestors AS (
         SELECT caf_accounts.account_id,
            caf_accounts.parent_id,
            caf_accounts.level
           FROM adempiere.caf_accounts
          WHERE (caf_accounts.level = 2)
        UNION ALL
         SELECT child.account_id,
            child.parent_id,
            (child.level + 1)
           FROM (adempiere.caf_accounts child
             JOIN ancestors p ON ((p.account_id = child.parent_id)))
        )
 SELECT acp.value AS parent_value,
    acp.name AS parent_name,
    ac.account_id,
    ac.value,
    ac.name,
    ac.issummary,
    ac.accounttype,
    ac.accountsign,
    ac.level,
    ac.parent_id
   FROM ((adempiere.caf_accounts ac
     LEFT JOIN ancestors an ON ((an.account_id = ac.account_id)))
     LEFT JOIN adempiere.caf_accounts acp ON ((acp.account_id = an.parent_id)))
  WHERE (ac.issummary = 'N'::bpchar)
  ORDER BY ac.value;


--
-- Name: caf_accounts_conami; Type: VIEW; Schema: adempiere; Owner: -
--

CREATE VIEW adempiere.caf_accounts_conami AS
 WITH RECURSIVE subset_accounts AS (
         SELECT caf_accounts.account_id,
            caf_accounts.value,
            caf_accounts.name,
            caf_accounts.issummary,
            caf_accounts.accounttype,
            caf_accounts.accountsign,
            caf_accounts.level,
            caf_accounts.parent_id
           FROM adempiere.caf_accounts
          WHERE ((caf_accounts.value)::text = ANY (ARRAY[('4106-01-01'::character varying)::text, ('1401-01-01'::character varying)::text, ('5401-00-00'::character varying)::text, ('5402-00-00'::character varying)::text, ('5403-00-00'::character varying)::text, ('5404-00-00'::character varying)::text, ('5405-00-00'::character varying)::text, ('2601-00-00'::character varying)::text, ('2605-00-00'::character varying)::text, ('2701-00-00'::character varying)::text, ('1100-00-00'::character varying)::text, ('1400-00-00'::character varying)::text, ('1600-00-00'::character varying)::text, ('1800-00-00'::character varying)::text, ('1900-00-00'::character varying)::text]))
        ), summary_accounts AS (
         SELECT subset_accounts.account_id,
            subset_accounts.name,
            subset_accounts.value
           FROM subset_accounts
          WHERE (subset_accounts.issummary = 'Y'::bpchar)
        ), descendant_accounts AS (
         SELECT ca.account_id,
            ca.issummary,
            sa.account_id AS conami_parent_id,
            sa.value AS conami_parent_value,
            sa.name AS conami_parent_name
           FROM (adempiere.caf_accounts ca
             JOIN summary_accounts sa ON ((ca.parent_id = sa.account_id)))
        UNION ALL
         SELECT ca.account_id,
            ca.issummary,
            da.conami_parent_id,
            da.conami_parent_value,
            da.conami_parent_name
           FROM (adempiere.caf_accounts ca
             JOIN descendant_accounts da ON ((ca.parent_id = da.account_id)))
        )
 SELECT subset_accounts.account_id,
    subset_accounts.value,
    subset_accounts.name,
    subset_accounts.issummary,
    subset_accounts.accounttype,
    subset_accounts.accountsign,
    subset_accounts.level,
    subset_accounts.parent_id,
    subset_accounts.account_id AS conami_parent_id,
    subset_accounts.value AS conami_parent_value,
    subset_accounts.name AS conami_parent_name
   FROM subset_accounts
  WHERE (subset_accounts.issummary = 'N'::bpchar)
UNION
 SELECT ca.account_id,
    ca.value,
    ca.name,
    ca.issummary,
    ca.accounttype,
    ca.accountsign,
    ca.level,
    ca.parent_id,
    da.conami_parent_id,
    da.conami_parent_value,
    da.conami_parent_name
   FROM (adempiere.caf_accounts ca
     JOIN descendant_accounts da ON ((ca.account_id = da.account_id)));


