CREATE VIEW adempiere.fact_acct_balance AS
 SELECT ad_client_id,
    ad_org_id,
    c_acctschema_id,
    adempiere.trunc(dateacct) AS dateacct,
    account_id,
    postingtype,
    m_product_id,
    c_bpartner_id,
    c_project_id,
    ad_orgtrx_id,
    c_salesregion_id,
    c_activity_id,
    c_campaign_id,
    c_locto_id,
    c_locfrom_id,
    user1_id,
    user2_id,
    gl_budget_id,
    COALESCE(sum(amtacctdr), (0)::numeric) AS amtacctdr,
    COALESCE(sum(amtacctcr), (0)::numeric) AS amtacctcr,
    COALESCE(sum(qty), (0)::numeric) AS qty,
    max(createdby) AS createdby,
    max(created) AS created,
    max(updatedby) AS updatedby,
    max(updated) AS updated,
    max(isactive) AS isactive,
    max(c_subacct_id) AS c_subacct_id,
    userelement1_id,
    userelement2_id,
    max(c_projectphase_id) AS c_projectphase_id,
    max(c_projecttask_id) AS c_projecttask_id
   FROM adempiere.fact_acct a
  GROUP BY ad_client_id, ad_org_id, c_acctschema_id, (adempiere.trunc(dateacct)), account_id, postingtype, m_product_id, c_bpartner_id, c_project_id, ad_orgtrx_id, c_salesregion_id, c_activity_id, c_campaign_id, c_locto_id, c_locfrom_id, user1_id, user2_id, userelement1_id, userelement2_id, gl_budget_id;


