--
-- Name: m_movement_candidate_v; Type: VIEW; Schema: adempiere; Owner: -
--

CREATE VIEW adempiere.m_movement_candidate_v AS
 SELECT o.ad_client_id,
    o.ad_org_id,
    o.c_bpartner_id,
    o.dd_order_id,
    o.documentno,
    o.dateordered,
    o.c_doctype_id,
    o.poreference,
    o.description,
    o.salesrep_id,
    l.m_locator_id,
    l.m_locatorto_id
   FROM (adempiere.dd_order o
     JOIN adempiere.dd_orderline l ON ((o.dd_order_id = l.dd_order_id)))
  WHERE ((o.docstatus = 'CO'::bpchar) AND (o.isdelivered = 'N'::bpchar) AND (o.c_doctype_id IN ( SELECT c_doctype.c_doctype_id
           FROM adempiere.c_doctype
          WHERE (c_doctype.docbasetype = 'DOO'::bpchar))) AND (o.deliveryrule <> 'M'::bpchar) AND ((l.m_product_id IS NULL) OR (EXISTS ( SELECT 1
           FROM adempiere.m_product p
          WHERE ((l.m_product_id = p.m_product_id) AND (p.isexcludeautodelivery = 'N'::bpchar))))) AND (l.qtyordered <> l.qtydelivered) AND (l.confirmedqty > (0)::numeric) AND (o.isdropship = 'N'::bpchar) AND ((l.m_product_id IS NOT NULL) OR (l.c_charge_id IS NOT NULL)) AND (NOT (EXISTS ( SELECT 1
           FROM (adempiere.m_movementline iol
             JOIN adempiere.m_movement io ON ((iol.m_movement_id = io.m_movement_id)))
          WHERE ((iol.dd_orderline_id = l.dd_orderline_id) AND (io.docstatus = ANY (ARRAY['IP'::bpchar, 'WC'::bpchar])))))))
  GROUP BY o.ad_client_id, o.ad_org_id, o.c_bpartner_id, o.dd_order_id, o.documentno, o.dateordered, o.c_doctype_id, o.poreference, o.description, o.salesrep_id, l.m_locator_id, l.m_locatorto_id;


