--
-- Name: m_product_bom; Type: VIEW; Schema: adempiere; Owner: -
--

CREATE VIEW adempiere.m_product_bom AS
 SELECT l.pp_product_bomline_id AS m_product_bom_id,
    l.ad_client_id,
    l.ad_org_id,
    l.isactive,
    l.created,
    l.createdby,
    l.updated,
    l.updatedby,
    l.line,
    b.m_product_id,
    l.m_product_id AS m_productbom_id,
    l.qtybom AS bomqty,
    b.description,
        CASE l.componenttype
            WHEN 'OP'::bpchar THEN 'O'::character varying
            WHEN 'CO'::bpchar THEN 'P'::character varying
            WHEN 'PH'::bpchar THEN 'P'::character varying
            WHEN 'PK'::bpchar THEN 'P'::character varying
            WHEN 'VA'::bpchar THEN
            CASE
                WHEN ((l.feature)::text = ANY ((ARRAY['1'::character varying, '2'::character varying, '3'::character varying, '4'::character varying, '5'::character varying, '6'::character varying, '7'::character varying, '8'::character varying, '9'::character varying])::text[])) THEN l.feature
                ELSE 'O'::character varying
            END
            ELSE 'P'::character varying
        END AS bomtype,
    l.pp_product_bomline_uu AS m_product_bom_uu
   FROM (adempiere.pp_product_bom b
     JOIN adempiere.pp_product_bomline l ON ((b.pp_product_bom_id = l.pp_product_bom_id)))
  WHERE ((b.bomtype = 'A'::bpchar) AND (b.bomuse = 'A'::bpchar) AND (b.isactive = 'Y'::bpchar));


