--
-- Name: m_rma_createfrom_v; Type: VIEW; Schema: adempiere; Owner: -
--

CREATE VIEW adempiere.m_rma_createfrom_v AS
 SELECT iol.m_inoutline_id,
    iol.line,
    iol.m_product_id,
    iol.c_charge_id,
    iol.m_attributesetinstance_id,
        CASE
            WHEN (iol.m_attributesetinstance_id IS NOT NULL) THEN ( SELECT asi.serno
               FROM adempiere.m_attributesetinstance asi
              WHERE (asi.m_attributesetinstance_id = iol.m_attributesetinstance_id))
            ELSE NULL::character varying
        END AS serno,
    iol.qtyentered,
    (iol.movementqty - COALESCE(( SELECT sum(rmal.qty) AS sum
           FROM (adempiere.m_rmaline rmal
             JOIN adempiere.m_rma rma ON ((rma.m_rma_id = rmal.m_rma_id)))
          WHERE ((rmal.m_inoutline_id = iol.m_inoutline_id) AND (rma.docstatus = ANY (ARRAY['CO'::bpchar, 'CL'::bpchar])))), (0)::numeric)) AS movementqty,
    iol.description,
    io.c_bpartner_id,
    iol.m_inoutline_id AS m_rma_createfrom_v_id,
    iol.isactive,
    iol.ad_client_id,
    iol.ad_org_id,
    io.m_inout_id
   FROM (adempiere.m_inoutline iol
     JOIN adempiere.m_inout io ON ((io.m_inout_id = iol.m_inout_id)));


