    (p.committedqty * cs.relativeweight) AS committedqty,
    (adempiere.currencyconvert(p.committedamt, p.c_currency_id, c.c_currency_id, statement_timestamp(), (0)::numeric, p.ad_client_id, p.ad_org_id) * cs.relativeweight) AS committedamt,
    (p.plannedqty * cs.relativeweight) AS plannedqty,
    (adempiere.currencyconvert(p.plannedamt, p.c_currency_id, c.c_currency_id, statement_timestamp(), (0)::numeric, p.ad_client_id, p.ad_org_id) * cs.relativeweight) AS plannedamt,
